INSERT INTO `threads` (`id`, `topic_id`, `user_id`, `title`, `content`, `date_created`) VALUES
(2, 4, 2, 'Đánh giá nhanh Video Joiner của Format Factory', '<p><strong>Format Factory</strong>&nbsp;l&agrave; ứng dụng hỗ trợ chuyển đổi video, audio, h&igrave;nh ảnh với chất lượng đầu ra ho&agrave;n hảo được giới chuy&ecirc;n m&ocirc;n đ&aacute;nh gi&aacute; rất cao. V&agrave; đ&atilde; l&acirc;u chưa viết b&agrave;i n&ecirc;n Quang Đức sẽ thực hiện lượt b&agrave;i đ&aacute;nh gi&aacute; nhanh về hoạt động của&nbsp;<strong>Format Factory</strong>&nbsp;phi&ecirc;n bản v.4.2.0 với chức năng&nbsp;<strong>Video Joiner</strong>&nbsp;để c&ugrave;ng chia sẻ với bạn đọc&hellip;<br />\r\n<br />\r\n<strong>Nh&agrave; ph&aacute;t h&agrave;nh:</strong>&nbsp;pcfreetime<br />\r\n<strong>Giấy ph&eacute;p:</strong>&nbsp;Miễn ph&iacute;&nbsp;<br />\r\n<strong>Dung lượng:</strong>&nbsp;47 MB<br />\r\n<br />\r\n<strong>Những điểm cần ch&uacute; &yacute; trong qu&aacute; tr&igrave;nh c&agrave;i đặt&nbsp;</strong></p>\r\n\r\n<p>K&iacute;ch hoạt tập tin c&agrave;i đặt&nbsp;<strong>FFSetup4.2.0.0.exe</strong>&nbsp;vừa tải về, hộp thoại&nbsp;<strong>Choose Install Location</strong>&nbsp;hiển thị &gt;&nbsp;<strong>Install</strong>, qu&aacute; tr&igrave;nh c&agrave;i đặt diễn ra nhanh ch&oacute;ng v&agrave; sau khi kết th&uacute;c &gt;&nbsp;<strong>Close</strong></p>\r\n\r\n<p>Hộp thoại&nbsp;<strong>Format Factory Install Codec</strong>&nbsp;hiển thị h&atilde;y k&iacute;ch bỏ chọn chức năng c&agrave;i đặt&nbsp;<strong>Format Factory</strong>&nbsp;v&agrave;o hệ thống tr&ecirc;n menu ngữ cảnh khi nhấp chuột phải v&agrave;o c&aacute;c tập tin bất kỳ, v&agrave; ứng dụng&nbsp;<strong>Picosmos Picture Tools</strong>&nbsp;(gi&uacute;p chỉnh sửa ảnh, ch&egrave;n hiệu ứng, gộp ảnh v&agrave; chụp m&agrave;n h&igrave;nh được cung cấp miễn ph&iacute; bởi pcfreetime) nếu kh&ocirc;ng muốn sử dụng &gt; OK, để bắt đầu trải nghiệm ứng dụng.<br />\r\n<br />\r\n<strong>Giao diện tương t&aacute;c</strong></p>\r\n\r\n<p><strong>Format Factory</strong>&nbsp;c&oacute; giao diện l&agrave;m việc đơn giản, dễ sử dụng đối với người d&ugrave;ng ở mọi lứa tuổi. Ứng dụng cung cấp c&aacute;c chức năng hỗ trợ chuyển đổi định dạng Video, &acirc;m thanh, h&igrave;nh ảnh, hoặc cho ph&eacute;p người d&ugrave;ng nối c&aacute;c tập tin Video hoặc những đoạn &acirc;m thanh tạo n&ecirc;n một tập tin &acirc;m thanh h&ograve;a trộn t&ugrave;y th&iacute;ch. Tiện dụng hơn nữa l&agrave; thực hiện đổi t&ecirc;n h&agrave;ng loạt c&aacute;c tập tin h&igrave;nh ảnh.<br />\r\n<br />\r\n<strong>Chức năng thường d&ugrave;ng</strong><br />\r\n<br />\r\nVới&nbsp;<strong>Format Factory</strong>&nbsp;người d&ugrave;ng sẽ dễ d&agrave;ng chuyển đổi định dạng c&aacute;c file h&igrave;nh ảnh, video, &acirc;m thanh hoặc văn bản dang PDF sang c&aacute;c định dạng chuẩn phổ th&ocirc;ng đang được người d&ugrave;ng sử dụng rộng r&atilde;i tr&ecirc;n to&agrave;n cầu với v&agrave;i thao t&aacute;c đơn giản, nhanh ch&oacute;ng. V&agrave; sau những trải nghiệm với chức năng&nbsp;<strong>Video Joiner</strong>&nbsp;từ c&aacute;c c&ocirc;ng cụ, ứng dụng kh&aacute;c như&nbsp;<strong>Bandicut</strong>,&nbsp;<strong>Xilisoft Video Converter</strong>&nbsp;th&igrave;&nbsp;<strong>Format Factory</strong>&nbsp;mang đến chất lượng h&igrave;nh ảnh ổn định v&agrave; dung lượng đầu ra của tập tin Video nhỏ hơn.</p>\r\n\r\n<p>B&agrave;i viết h&ocirc;m nay chỉ xoay quanh chức năng được Quang Đức thường d&ugrave;ng ch&iacute;nh l&agrave;&nbsp;<strong>Video Joiner</strong>, v&agrave; c&aacute;c file minh họa ch&iacute;nh l&agrave; c&aacute;c đoạn video quay lại hậu quả của cơn b&atilde;o số 12 vừa qua tại tỉnh&nbsp;<strong>Kh&aacute;nh H&ograve;a</strong>&nbsp;ngay trung t&acirc;m cơn b&atilde;o.<br />\r\nSau khi đ&atilde; chọn được c&aacute;c tập tin video muốn sử dụng nhấn Open tr&ecirc;n hộp thoại để ch&egrave;n ch&uacute;ng v&agrave;o Format Factory v&agrave; sau khi đ&atilde; kiểm tra, đồng &yacute; nhấn OK.</p>\r\n\r\n<p>V&agrave; những t&iacute;nh năng ưu việt nhất của&nbsp;<strong>Video Joiner</strong>&nbsp;đang chờ người d&ugrave;ng kh&aacute;m ph&aacute; để c&oacute; thể thiết lập chất lượng đoạn video muốn thực hiện theo &yacute; th&iacute;ch c&aacute; nh&acirc;n th&ocirc;ng qua thao t&aacute;c nhấn v&agrave;o mũi t&ecirc;n trỏ xuống để chọn định dạng cho tập tin video.</p>\r\n\r\n<p>Sau đ&oacute; chọn tiếp khung Setting kế b&ecirc;n để thực hiện c&aacute;c thiết lập c&aacute; nh&acirc;n l&ecirc;n tập tin video đầu ra t&ugrave;y thuộc v&agrave;o mức độ am hiểu của người d&ugrave;ng th&ocirc;ng qua việc nhấn chọn c&aacute;c thuộc t&iacute;nh đang hiển thị tr&ecirc;n cửa sổ Video Setting rồi thay đổi c&aacute;c th&ocirc;ng số được cung cấp sẵn, nhấn OK để x&aacute;c nhận c&aacute;c thiết lập.</p>\r\n\r\n<p>Tại cửa sổ Output Setting &gt; OK lần nữa để x&aacute;c nhận c&aacute;c bước đ&atilde; thiết lập trước đ&oacute;.</p>\r\n\r\n<p>Trở lại giao diện ch&iacute;nh của&nbsp;<strong>Format Factory</strong>, người d&ugrave;ng thấy th&ocirc;ng tin dung lượng của c&aacute;c đoạn video muốn thực hiện v&agrave; thư mục lưu trữ đ&atilde; được thiết lập trước đ&oacute;, h&atilde;y chọn n&uacute;t Start để thực hiện. Thời gian thực hiện nhanh hay chậm t&ugrave;y thuộc v&agrave;o độ d&agrave;i, chất lượng video đ&atilde; được c&aacute; nh&acirc;n h&oacute;a tại hộp thoại Setting, v&agrave; cấu h&igrave;nh của m&aacute;y (thời gian thực hiện của 4 tập tin minh họa c&oacute; dung tổng dung lượng 4.6GB l&agrave; 20 ph&uacute;t v&agrave; dung lượng chỉ c&oacute; 4.33 GB).</p>\r\n\r\n<p>Tập tin Video đầu ra c&oacute; dung lượng 4.33GB đ&atilde; được thiết lập với c&aacute;c th&ocirc;ng số cao nhất như minh họa để mang đến chất lượng h&igrave;nh ảnh mượt m&agrave;, r&otilde; n&eacute;t nhất. Nếu người d&ugrave;ng cứ để mặc định th&igrave; dung lượng đầu ra của tập tin video rất nhỏ đồng nghĩa với việc chất lượng h&igrave;nh ảnh cũng giảm s&uacute;t đ&aacute;ng kể.<br />\r\n<br />\r\n<strong>Nhận x&eacute;t</strong><br />\r\n<br />\r\nQuang Đức mượn c&aacute;c h&igrave;nh ảnh minh họa vừa để gợi &yacute; c&aacute;c bước sử dụng gi&uacute;p người d&ugrave;ng c&oacute; thể tương t&aacute;c với&nbsp;<strong>Format Factory</strong>&nbsp;nhanh ch&oacute;ng, v&agrave; th&ocirc;ng qua đ&oacute; diễn đạt những hiệu quả, &iacute;ch lợi trong c&aacute;c t&aacute;c vụ nhằm r&uacute;t gọn thời gian thực hiện v&agrave; n&acirc;ng cao chất lượng c&ocirc;ng việc h&agrave;ng ng&agrave;y.&nbsp;<strong>Format Factory</strong>&nbsp;l&agrave; một ứng dụng hỗ trợ xử l&yacute;, chuyển đổi định dạng c&aacute;c tập tin ảnh, video, &acirc;m thanh tuyệt vời nhất rất đ&aacute;ng để sử dụng.<br />\r\n<br />\r\nBạn h&atilde;y tải&nbsp;<strong>Format Factory</strong>&nbsp;về c&agrave;i đặt v&agrave; c&ugrave;ng trải nghiệm với c&aacute;c chức năng c&ograve;n lại để tận hưởng th&agrave;nh quả mang lại từ ch&iacute;nh ứng dụng nh&eacute;.</p>\r\n', '2018-05-21 13:51:53'),
(3, 4, 2, 'Đánh giá phần mềm nhận key Windows 10 Pro & Office 2016', '<p><span style=\"font-size:13px\"><span style=\"color:#333333\"><span style=\"font-family:Verdana,Arial,Tahoma,Calibri,Geneva,sans-serif\"><span style=\"background-color:#ffffff\">Để tiếp tục tạo phong tr&agrave;o AE tự viết b&agrave;i đ&aacute;nh gi&aacute; c&aacute;c phần mềm đang được c&agrave;i đặt, sử dụng tr&ecirc;n m&aacute;y t&iacute;nh BQT tạo event để AE thảnh vi&ecirc;n v&agrave; bạn đọc c&oacute; thể tham gia nhằm đ&oacute;ng g&oacute;p cho dữ liệu của subbox Đ&aacute;nh Gi&aacute; Phần Mềm ng&agrave;y c&agrave;ng phong ph&uacute; với mọi thể loại kh&aacute;c nhau.</span></span></span></span><br />\r\n<br />\r\n<strong>Thể lệ</strong><br />\r\n<br />\r\n<span style=\"font-size:13px\"><span style=\"color:#333333\"><span style=\"font-family:Verdana,Arial,Tahoma,Calibri,Geneva,sans-serif\"><span style=\"background-color:#ffffff\">_ Kh&ocirc;ng ph&acirc;n biệt cấp độ th&agrave;nh vi&ecirc;n d&ugrave; đ&oacute; l&agrave; nick mới đăng k&yacute; hay đ&atilde; tham gia diễn đ&agrave;n từ 2006.</span></span></span></span><br />\r\n<span style=\"font-size:13px\"><span style=\"color:#333333\"><span style=\"font-family:Verdana,Arial,Tahoma,Calibri,Geneva,sans-serif\"><span style=\"background-color:#ffffff\">_ AE th&agrave;nh vi&ecirc;n v&agrave; bạn đọc c&oacute; thể đ&aacute;nh gi&aacute; bất kỳ một ứng dụng n&agrave;o đang được c&agrave;i đặt tr&ecirc;n m&aacute;y t&iacute;nh để chia sẻ th&ocirc;ng tin với cộng đồng từ miễn ph&iacute;, bản quyền hay sử dụng qua c&aacute;c c&ocirc;ng cụ bẻ kh&oacute;a.&nbsp;</span></span></span></span><br />\r\n<span style=\"font-size:13px\"><span style=\"color:#333333\"><span style=\"font-family:Verdana,Arial,Tahoma,Calibri,Geneva,sans-serif\"><span style=\"background-color:#ffffff\">_ Để tham gia AE th&agrave;nh vi&ecirc;n, bạn đọc y&ecirc;u c&ocirc;ng nghệ chỉ cần post b&agrave;i tại subbox&nbsp;</span></span></span></span><a href=\"http://www.vn-zoom.com/register.php\" onclick=\"alert(\'You made the correct decision :)\')\" style=\"box-shadow:none !important; color:#e12b00; text-decoration:none; font-weight:400; font-family:Verdana, Arial, Tahoma, Calibri, Geneva, sans-serif; font-size:13px; font-style:normal; font-variant-ligatures:normal; font-variant-caps:normal; letter-spacing:normal; orphans:2; text-align:left; text-transform:none; white-space:normal; widows:2; word-spacing:0px; -webkit-text-stroke-width:0px; background-color:#ffffff\"><input style=\"margin:0px\" type=\"button\" value=\"Hãy đăng ký làm thành viên để xem được liên kết này\" /></a><span style=\"font-size:13px\"><span style=\"color:#333333\"><span style=\"font-family:Verdana,Arial,Tahoma,Calibri,Geneva,sans-serif\"><span style=\"background-color:#ffffff\">, sau đ&oacute; copy link của topic d&aacute;n v&agrave;o comments tham gia tại event để BQT kiểm tra, đ&aacute;nh gi&aacute; nội dung b&agrave;i viết.</span></span></span></span><br />\r\n<span style=\"font-size:13px\"><span style=\"color:#333333\"><span style=\"font-family:Verdana,Arial,Tahoma,Calibri,Geneva,sans-serif\"><span style=\"background-color:#ffffff\">_ C&aacute;c b&agrave;i viết được tr&igrave;nh b&agrave;y r&otilde; r&agrave;ng, k&egrave;m theo h&igrave;nh ảnh minh họa c&aacute;c bước trải nghiệm chụp từ m&agrave;n h&igrave;nh sẽ được đ&aacute;nh gi&aacute; cao trong l&uacute;c chấm điểm.</span></span></span></span><br />\r\n<span style=\"font-size:13px\"><span style=\"color:#333333\"><span style=\"font-family:Verdana,Arial,Tahoma,Calibri,Geneva,sans-serif\"><span style=\"background-color:#ffffff\">_ AE được quyền ưu ti&ecirc;n 2 lượt edit nội dung b&agrave;i viết cho ho&agrave;n chỉnh trong suốt thời gian tham gia event t&iacute;nh từ thời điểm AE comments tại topic đăng k&yacute; n&agrave;y.</span></span></span></span><br />\r\n<span style=\"font-size:13px\"><span style=\"color:#333333\"><span style=\"font-family:Verdana,Arial,Tahoma,Calibri,Geneva,sans-serif\"><span style=\"background-color:#ffffff\">_ AE được quyền tham gia với nhiều topic đ&aacute;nh gi&aacute; những phần mềm, ứng dụng kh&aacute;c nhau miễn sao kh&ocirc;ng tr&ugrave;ng khớp nội dung l&agrave; được.</span></span></span></span><br />\r\n<br />\r\n<strong>Những điều cần tr&aacute;nh<br />\r\n<br />\r\n* Hạn chế sao ch&eacute;p nội dung c&aacute;c b&agrave;i viết của th&agrave;nh vi&ecirc;n kh&aacute;c đ&atilde; được post tại VNZ hoặc c&aacute;c diễn đ&agrave;n kh&aacute;c nhằm tr&aacute;nh những t&igrave;nh huống thưa kiện. Nếu được ph&aacute;t hiện hoặc bị th&agrave;nh vi&ecirc;n kh&aacute;c thưa kiện buộc l&ograve;ng BTC sẽ hạ b&agrave;i v&agrave; cảnh c&aacute;o point th&agrave;nh vi&ecirc;n đ&atilde; vi phạm.<br />\r\n* Những topic vi phạm việc edit nội dung qu&aacute; 3 lần cho ph&eacute;p khi bị ph&aacute;t hiện th&igrave; b&agrave; viết sẽ bị loại khỏi danh s&aacute;ch tham gia event.</strong><br />\r\n<br />\r\n<br />\r\n<span style=\"color:#0000ff\"><span style=\"font-size:x-large\"><strong>Giải thưởng</strong><br />\r\n<br />\r\n<strong>* Giải nhất sẽ nhận được 1 key Windows 10 Pro Retail &amp; 1 key Office Pro Plus 2016 Retail<br />\r\n* Gi&aacute;i nh&igrave; l&agrave; 1 key Windows 10 Pro Retail</strong></span></span><br />\r\n<br />\r\n<br />\r\n<strong>Event sẽ bắt đầu ngay sau khi được hiển thị v&agrave; kết th&uacute;c v&agrave;o cuối ng&agrave;y&nbsp;<span style=\"color:#0000ff\">28/04/2016</span>&nbsp;v&agrave; sẽ th&ocirc;ng b&aacute;o kết quả ng&agrave;y&nbsp;<span style=\"color:#0000ff\">30/04/2016</span>. Những th&agrave;nh vi&ecirc;n may mắn nhận giải sẽ được BQT li&ecirc;n hệ bằng c&aacute;ch th&ocirc;ng b&aacute;o trực tiếp tại topic v&agrave; qua tin nhắn trang c&aacute; nh&acirc;n v&agrave; phải hồi &acirc;m bằng c&aacute;ch gửi địa chỉ email để nhận qu&agrave;. Nếu vượt qu&aacute; 72 giờ t&iacute;nh từ thời điểm BQT gửi tin nhắn AE được nhận giải kh&ocirc;ng hồi &acirc;m, BQT sẽ thu hồi qu&agrave; để trao cho th&agrave;nh vi&ecirc;n kh&aacute;c hoặc lưu lại để l&agrave;m qu&agrave; cho những event kế tiếp.</strong><br />\r\n<br />\r\n<br />\r\n<span style=\"font-size:13px\"><span style=\"color:#333333\"><span style=\"font-family:Verdana,Arial,Tahoma,Calibri,Geneva,sans-serif\"><span style=\"background-color:#ffffff\">Tr&acirc;n trọng</span></span></span></span></p>\r\n', '2018-05-21 15:42:30'),
(4, 4, 3, '[Review] Đánh Giá Về Phần Mềm Google Earth Pro (Phần mềm ít sài nhưng ứng dụng rất lớn)', '<p>Phần I. Lời n&oacute;i đầu Phần lớn mọi người trong ch&uacute;ng ta đều đ&atilde; từng sử dụng chương tr&igrave;nh lập bản đồ trực tuyến một lần trong đời. Đ&acirc;y thật l&agrave; một m&oacute;n qu&agrave; qu&yacute; gi&aacute; m&agrave; thượng đế ban tặng những người thường xuy&ecirc;n đi lạc khi vượt ra ngo&agrave;i b&aacute;n k&iacute;nh 3 dặm quanh nh&agrave;, v&agrave; l&agrave; một kho t&agrave;ng v&ocirc; tận cho những người th&iacute;ch lục lọi những h&igrave;nh ảnh kỳ lạ, th&uacute; vị. Bạn cũng c&oacute; thể tưởng tượng được sự kinh ngạc m&agrave; Google Earth Pro đem lại cho cộng đồng Internet. Google Earth Pro kh&ocirc;ng phải l&agrave; một ứng dụng bản đồ b&igrave;nh thường. Cứ thử g&otilde; &quot;Denver, CO&quot; v&agrave;o &ocirc; t&igrave;m kiếm của Google Earth, v&agrave; bạn sẽ biết được vị tr&iacute; của Denver, Colo so với Grand Junction, Colo. cũng như c&aacute;ch để đi từ địa chỉ nh&agrave; bạn tới số 1600 phố Curtis, trung t&acirc;m th&agrave;nh phố Denver. Tương tự như vậy, bạn cũng sẽ biết được số 1600 phố Curtis c&oacute; m&atilde; bưu điện l&agrave; 80202, v&agrave; c&oacute; 12 cửa h&agrave;ng caf&eacute; Starbucks trong v&ograve;ng b&aacute;n k&iacute;nh nửa dặm t&iacute;nh từ địa chỉ đ&oacute;. Nếu muốn, bạn c&oacute; thể zoom out từ địa chỉ 1600 phố Curtis v&agrave; chứng kiến n&oacute; dần dần ch&igrave;m v&agrave;o tầm ngắm vệ tinh của tr&aacute;i đất, rồi lại zoom v&agrave;o như thể bạn đang rơi từ vũ trụ xuống n&oacute;c nh&agrave; số 1600 phố Curtis vậy. C&ograve;n nếu bạn click l&ecirc;n &quot;Forbidden City&quot; (Tử cấm th&agrave;nh) trong cột &quot;Sightseeing&quot;, bạn sẽ được trải nghiệm c&aacute;i cảm gi&aacute;c bay như si&ecirc;u nh&acirc;n từ số 1600 phố Curtis ở Denver đến Tử cấm th&agrave;nh ở Bắc Kinh, Trung Quốc. V&agrave; rồi bạn chợt nghĩ rằng thế giới thật nhỏ b&eacute;. . Ứng dụng n&agrave;y rất dễ sử dụng, nhưng ẩn chứa v&ocirc; số những chức năng th&uacute; vị chờ bạn kh&aacute;m ph&aacute;. Vậy h&atilde;y c&ugrave;ng kh&aacute;m ph&aacute; xem bạn c&oacute; thể thu được g&igrave; từ Google Earth Pro 1. Giới Thiệu. Google Earth Pro l&agrave; một h&igrave;nh ảnh địa cầu tương t&aacute;c 3D c&oacute; thể được sử dụng để hỗ trợ lập kế hoạch, ph&acirc;n t&iacute;ch v&agrave; ra quyết định. C&aacute;c doanh nghiệp, ********* v&agrave; người d&ugrave;ng chuy&ecirc;n nghiệp từ khắp nơi tr&ecirc;n thế giới sử dụng c&aacute;c c&ocirc;ng cụ chia sẻ th&ocirc;ng tin, lập kế hoạch trang web v&agrave; trực quan h&oacute;a dữ liệu của Google Earth Pro. Hiện tại bạn c&oacute; thể download về m&aacute;y t&iacute;nh (laptop) của m&igrave;nh, nhưng vẫn phải kết nối Internet để sử dụng n&oacute;. Mỗi lần bạn mở Google Earth Pro, n&oacute; sẽ tự động kết nối với m&aacute;y chủ của Google, gi&uacute;p bạn truy cập v&agrave;o h&agrave;ng terabyte dữ liệu địa l&yacute;, *********, v&agrave; xuất hiện. V&iacute; dụ như, bạn c&oacute; thể ngắm một th&agrave;nh phố khi đang k&iacute;ch hoạt một số &ldquo;lớp&rdquo; nhất định, bao gồm th&ocirc;ng tin về địa h&igrave;nh, d&acirc;n số, v&agrave; số liệu tội phạm của khu vực đ&oacute;. C&aacute;c lớp n&agrave;y, c&ugrave;ng với tất cả c&aacute;c ph&iacute;m định vị tr&ecirc;n bản đồ, bao gồm zoom hay quay tr&ograve;n, đều nằm tr&ecirc;n khung h&igrave;nh Google Earth Pro. Chiếm dụng t&agrave;i nguy&ecirc;n: V&igrave; phần mềm chỉ sử dụng khi mở n&ecirc;n kh&ocirc;ng chạy ẩn về nhận x&eacute;t ta c&oacute; thể tạm chấp nhận được Đ&aacute;nh gi&aacute; băng phần mềm AverageCPUcycles : Qua th&ocirc;ng số t&agrave;i nguy&ecirc;n trung b&igrave;nh m&agrave; n&oacute; đ&atilde; sử dung, th&ocirc;ng số MHz đang sử dụng&hellip; th&igrave; theo m&igrave;nh l&agrave; kh&ocirc;ng chiếm nhiều lắm, tuy nhi&ecirc;n phần mềm chạy độc lập v&agrave; kh&ocirc;ng chạy ẩn n&ecirc;n kh&ocirc;ng xung đột với b&acirc;t k&igrave; ứng dụng hệ thống n&agrave;o. Cấu h&igrave;nh hệ thống PC Tối thiểu: Hệ điều h&agrave;nh: Windows XP trở l&ecirc;n CPU: Pentium 3, 500Mhz B&ocirc;̣ nhớ h&ecirc;̣ th&ocirc;́ng (RAM): 512MB Đĩa cứng: dung lượng tr&ocirc;́ng 500MB T&ocirc;́c đ&ocirc;̣ mạng: 128 Kbit/gi&acirc;y Cạc đồ họa: DirectX9 v&agrave; tương th&iacute;ch 3D với 64MB VRAM M&agrave;n h&igrave;nh: 1024x768, &quot;16-bit High Color&quot; - DirectX 9 (để chạy ở chế độ Direct X) Thư: Y&ecirc;u cầu Outlook 2007+ cho chức năng email Được đề xuất: Hệ điều h&agrave;nh: Windows 7 v&agrave; 8 CPU: Pentium 4 GHz+ hoặc AMD 2400xp+ B&ocirc;̣ nhớ h&ecirc;̣ th&ocirc;́ng (RAM): 1GB+ Đĩa cứng: dung lượng trống 2GB+ T&ocirc;́c đ&ocirc;̣ mạng: 768 Kbit/gi&acirc;y Cạc đồ họa: DirectX9 v&agrave; tương th&iacute;ch 3D với 256MB VRAM M&agrave;n h&igrave;nh: 1280x1024, &quot;32-bit True Color&quot; C&aacute;i ch&iacute;nh ở đ&acirc;y l&agrave; Google Earth Pro bay giờ l&agrave; ho&agrave;n to&agrave;n miễn ph&iacute;. trước đ&acirc;y n&oacute; c&oacute; gi&aacute; kh&aacute; cao khoản 399$ cho một phi&ecirc;n bản Pro Link tải : Lưu &yacute;: Google Earth Pro y&ecirc;u cầu phải c&oacute; m&atilde; kh&oacute;a giấy ph&eacute;p. Nếu bạn kh&ocirc;ng c&oacute; m&atilde; kh&oacute;a, h&atilde;y sử dụng địa chỉ email của m&igrave;nh v&agrave; m&atilde; kh&oacute;a GEPFREE để đăng nhập. 2. Về c&aacute;c chức năng của n&oacute; Sau khi mở Google Earth Pro v&agrave; bắt đầu quan s&aacute;t, bạn sẽ thấy một trong số những vấn đề lớn nhất của chương tr&igrave;nh n&agrave;y: một số th&ocirc;ng tin ở dạng 3-D, v&agrave; những th&ocirc;ng tin n&agrave;o kh&ocirc;ng ở dạng 3-D th&igrave; vẫn chỉ l&agrave; những tấm ảnh &ndash; kh&ocirc;ng phải bản đồ minh hoạ. Dữ liệu h&igrave;nh ảnh cơ sở được chụp từ vệ tinh v&agrave; m&aacute;y bay. Mặc d&ugrave; Google Earth Pro chứa một số lượng lớn t&iacute;nh năng m&agrave; bạn sẽ phải mất cả th&aacute;ng trời mới khai th&aacute;c hết được, th&igrave; những t&iacute;nh năng cơ bản thuộc v&agrave;o một trong số những danh mục sau đ&acirc;y: &middot; Bản đồ/Chỉ hướng &middot; T&igrave;m kiếm địa phương &middot; Danh lam thắng cảnh Những t&iacute;nh năng cơ bản n&agrave;y. C&aacute;ch tốt nhất để l&agrave;m quen vớiGoogle Earth Pro l&agrave; bắt đầu từ một địa điểm cụ thể, v&agrave; xem Google Earth Pro cho bạn biết những g&igrave; về địa điểm đ&oacute;. V&igrave; thế h&atilde;y bắt đầu với đại bản doanh của HowStuffWorks tại Atlanta, Ga. Nếu bạn g&otilde; &quot;3350 Peachtree Rd. Atlanta, GA,&quot; v&agrave;o &ocirc; t&igrave;m kiếm của Google Earth rồi nhấn &quot;Search,&quot; v&agrave; zoom từ ngo&agrave;i vũ trụ v&agrave;o trụ sở HowStuffWorks. Giờ đ&acirc;y bạn đ&atilde; c&oacute; một tấm ảnh của địa điểm n&agrave;y Để biết th&ecirc;m c&aacute;c th&ocirc;ng tin kh&aacute;c v&agrave; chuyển bức ảnh tr&ecirc;n th&agrave;nh một bản đồ thực sự, bước tiếp theo l&agrave; k&iacute;ch hoạt một số lớp cần thiết. C&aacute;c lớp sẵn c&oacute; gồm nơi ăn uống, nghỉ trọ, ng&acirc;n h&agrave;ng, đường phố, địa h&igrave;nh, trung t&acirc;m mua sắm, cửa h&agrave;ng rau quả, trạm xăng, c&ocirc;ng vi&ecirc;n, phương tiện c&ocirc;ng cộng v&agrave; một số đặc điểm địa l&yacute; kh&aacute;c. Để tạo được một tấm bản đồ đẹp mắt với đầy đủ chức năng, c&oacute; lẽ bạn chỉ cần k&iacute;ch hoạt lớp &ldquo;roads&quot; (đường phố). Nếu zoom out một ch&uacute;t, bạn sẽ c&oacute; một tấm bản đồ l&aacute;i xe trong một khung vực rộng lớn bao quanh số 3350 đường Peachtree ở Atlanta. Tại Mỹ c&oacute; thể địa chỉ ho&aacute; tới những vị tr&iacute; tr&ecirc;n những th&agrave;nh phố lớn , nhưng tại Việt nam , Google chưa l&agrave;m được điều n&agrave;y . Bạn chỉ c&oacute; thể v&iacute; dụ t&igrave;m theo từ kho&aacute; Hanoi để tới thủ đ&ocirc; H&agrave; nội , sau đ&oacute; dựa v&agrave;o những điểm mốc tr&ecirc;n bản đồ để t&igrave;m tới những vị tr&iacute; m&igrave;nh cần Những th&ocirc;ng tin kh&aacute;c G-E-P gi&uacute;p bạn l&agrave;m việc hiệu quả hơn với c&ocirc;ng cụ vẽ v&agrave; đo lường n&acirc;ng cao trong Google Earth Pro, bạn c&oacute; thể lập kế hoạch, đo lường v&agrave; trực quan h&oacute;a trang web m&agrave; kh&ocirc;ng cần rời khỏi b&agrave;n l&agrave;m việc của bạn. 1. Đo khoảng c&aacute;ch T&iacute;nh to&aacute;n khoảng c&aacute;ch bằng bộ, dặm, ki l&ocirc; m&eacute;t, mẫu v&agrave; nhiều hơn nữa. Lập kế hoạch chiều d&agrave;i của một đường băng mới, khoảng c&aacute;ch giữa c&aacute;c tua-bin gi&oacute; hoặc chu vi của một trung t&acirc;m thương mại b&aacute;n lẻ mới. 2. Đo diện t&iacute;ch Đo b&aacute;n k&iacute;nh v&agrave; diện t&iacute;ch một c&aacute;ch nhanh ch&oacute;ng để x&aacute;c định v&ugrave;ng đệm cho một nh&agrave; m&aacute;y năng lượng hoặc lượng xi măng cần thiết cho một b&atilde;i đậu xe mới. 3.Đo 3D X&aacute;c định chiều cao của một t&ograve;a nh&agrave;, đo tầm nh&igrave;n từ t&ograve;a nh&agrave; d&acirc;n cư cao tầng mới đến c&ocirc;ng vi&ecirc;n gần nhất, hoặc từ phương ngắm ra đại dương. 4. Ra quyết định tốt hơn C&aacute;c lớp dữ liệu cao cấp trong Google Earth Pro cho ph&eacute;p bạn đưa ra nhiều quyết định c&oacute; hiểu biết trong bối cảnh vị tr&iacute; 5. Dữ liệu l&ocirc; đất Nghi&ecirc;n cứu đất đai một c&aacute;ch dễ d&agrave;ng bằng c&aacute;ch ph&oacute;ng to để xem ranh giới l&ocirc; đất đối với h&agrave;ng triệu vị tr&iacute; ở Hoa Kỳ. Nhấp v&agrave;o biểu tượng để xem gi&aacute; b&aacute;n cuối c&ugrave;ng, ph&acirc;n v&ugrave;ng, bộ vu&ocirc;ng v&agrave; nhiều hơn nữa. C&aacute;c b&aacute;o c&aacute;o bổ sung về l&ocirc; đất c&oacute; thể thu được từ DataQuick. 6. Dữ liệu nh&acirc;n khẩu học C&aacute;c bản đồ chuy&ecirc;n đề dễ đọc hiển thị c&aacute;c trường dữ liệu nh&acirc;n khẩu học được Nielsen Claritas cung cấp. Đưa ra quyết định kinh doanh bằng c&aacute;ch sử dụng độ tuổi trung b&igrave;nh, thu nhập, tr&igrave;nh độ học vấn v&agrave; nhiều hơn nữa cho tiểu bang, hạt, khu vực thống k&ecirc; hoặc nh&oacute;m khối. 7. C&ocirc;ng cụ ph&acirc;n t&iacute;ch tầm nh&igrave;n Dễ d&agrave;ng x&aacute;c định v&agrave; t&iacute;nh to&aacute;n điểm nh&igrave;n, đo khoảng c&aacute;ch v&agrave; trực quan h&oacute;a phương ngắm 360 độ một c&aacute;ch dễ d&agrave;ng để giải th&iacute;ch chế độ xem 3D theo m&atilde; m&agrave;u 7. Dữ liệu đếm lưu lượng truy cập Hiểu xu hướng lưu lượng truy cập theo lịch sử bằng c&aacute;ch sử dụng c&aacute;c biểu tượng dễ đọc v&agrave; đ&atilde; được lập bản đồ. Hiểu c&aacute;c bộ dữ liệu lớn bằng c&aacute;ch trực quan h&oacute;a ch&uacute;ng tr&ecirc;n h&igrave;nh ảnh địa cầu. Lớp Số lượng truy cập cung cấp cho bạn quyền truy cập v&agrave;o dữ liệu số lượng truy cập hơn 30 năm, do Market Planning Solutions Inc. cung cấp. 8. Nhập dữ liệu của ri&ecirc;ng bạn Google Earth Pro cho ph&eacute;p bạn dễ d&agrave;ng nhập dữ liệu v&agrave; tạo kiểu dựa tr&ecirc;n c&aacute;c ti&ecirc;u ch&iacute; quan trọng Đa gi&aacute;c, đường kẻ &amp; đường dẫn Nhập c&aacute;c tệp ESRI Shape (SHP), MapInfo (TAB), Ng&ocirc;n ngữ đ&aacute;nh dấu lỗ kh&oacute;a (KML) v&agrave; c&aacute;c tệp kh&aacute;c v&agrave; để cho Vector Data Regionation tự động tối ưu h&oacute;a v&agrave; ph&acirc;n cấp tải c&aacute;c điểm để cải thiện hiệu suất Những chức năng &quot;kh&ocirc;ng thể tin được&quot; Video Flyovers Cộng đồng Google Earth c&oacute; hẳn một forum d&agrave;nh cho người d&ugrave;ng để b&agrave;n về c&aacute;c t&iacute;nh năng của chương tr&igrave;nh, chia sẻ file, c&aacute;c mẹo t&igrave;m h&igrave;nh ảnh v&agrave; trưng b&agrave;y những tấm ảnh độc đ&aacute;o. H&atilde;y v&agrave;o Google Earth Community để tham gia. Một trong số những t&iacute;nh năng đ&aacute;ng kinh ngạc nhất của Google Earth Pro l&agrave; flyover. V&iacute; dụ khi bạn đang xem bản đồ th&agrave;nh phố Atlanta, Ga., v&agrave; g&otilde; từ kho&aacute; &ldquo;Nepal&rdquo; v&agrave;o &ocirc; t&igrave;m kiếm, thế l&agrave; chỉ trong nh&aacute;y mắt bạn đ&atilde; ở Nepal. Chương tr&igrave;nh n&agrave;y sẽ đưa bạn bay đến đ&oacute;, v&igrave; thế bạn c&oacute; thể quan s&aacute;t to&agrave;n bộ v&ugrave;ng l&atilde;nh thổ c&ugrave;ng những quốc gia ph&iacute;a dưới, tựa như khi bạn ở tr&ecirc;n m&aacute;y bay nh&igrave;n xuống vậy. Ở cuối m&agrave;n h&igrave;nh chỉ hướng đi, c&oacute; một ph&iacute;m &ldquo;play&rdquo;. Nếu bạn click ph&iacute;m n&agrave;y, Google Earth Pro sẽ đưa bạn bay qua con đường đ&atilde; định, quẹo tr&aacute;i quẹo phải đ&uacute;ng như lịch tr&igrave;nh, v&igrave; thế bạn c&oacute; thể quan s&aacute;t ch&iacute;nh x&aacute;c đường đi của m&igrave;nh. 3-D Views Google Earth Pro c&oacute; phi&ecirc;n bản 3-D cho tất cả c&aacute;c th&agrave;nh phố ở Mỹ. Phần lớn ch&uacute;ng kh&ocirc;ng phải l&agrave; c&aacute;c bản sao chi tiết - chỉ l&agrave; những khối h&igrave;nh 3-D đơn giản x&aacute;m xịt, nhưng ch&iacute; &iacute;t cũng đủ để l&agrave;m bạn cảm nhận r&otilde; hơn c&aacute;c th&agrave;nh phố n&agrave;y. Hiện nay Google Earth Pro đ&atilde; hỗ trợ kết cấu 3-D (h&igrave;nh ảnh thực). Một số h&igrave;nh ảnh nằm sẵn trong ứng dụng, nhưng giờ đ&acirc;y Google đ&atilde; cho ph&eacute;p người d&ugrave;ng tự tạo v&agrave; nhập h&igrave;nh ảnh 3-D của m&igrave;nh bằng c&aacute;ch sử dụng chương tr&igrave;nh miễn ph&iacute; Google SketchUp. Cũng như với c&aacute;c h&igrave;nh ảnh kh&aacute;c, bạn c&oacute; thể sử dụng ph&iacute;m &quot;tilt&quot; v&agrave; &quot;rotate&quot; tr&ecirc;n panel định hướng để c&oacute; được to&agrave;n cảnh h&igrave;nh ảnh 3-D. Sightseeing Across the Globe C&ograve;n nếu bạn chưa c&oacute; thời gian để đi du lịch v&ograve;ng quanh thế giới th&igrave; Google Earth Pro ch&iacute;nh l&agrave; người bạn th&acirc;n thiết của bạn. Bạn c&oacute; thể zoom v&agrave;o c&aacute;c địa điểm du lịch nổi tiếng thế giới bằng c&aacute;ch chọn một địa điểm trong &ocirc; &quot;Sightseeing&quot;, gồm những địa điểm du lịch nổi tiếng nhất như th&aacute;p Eiffel ở Paris, khe n&uacute;i Grand ở Arizona v&agrave; th&agrave;nh phố Vatican. Google Sky Google Earth Pro gi&uacute;p người d&ugrave;ng c&oacute; thể bay đến bất cứ nơi n&agrave;o tr&ecirc;n tr&aacute;i đất. V&agrave; đến th&aacute;ng 8 năm 2007, Google tiếp tục giới thiệu Google Sky ( bấm menu View của Google Earth Pro , chọn Switch to Sky ) , t&iacute;nh năng gi&uacute;p người d&ugrave;ng đi du lịch v&ograve;ng quanh vũ trụ. Chỉ bằng một c&uacute; click chuột, người d&ugrave;ng Google Earth Pro c&oacute; thể chuyển sang Sky Mode v&agrave; ngược lại. Với những h&igrave;nh ảnh do NASA, Hiệp hội khảo s&aacute;t số v&agrave; Khảo s&aacute;t bầu trời Sloan Digital Sky Survey cung cấp, Google Earth Pro đ&atilde; tổng hợp được một c&aacute;i nh&igrave;n to&agrave;n cảnh khổng lồ về vũ trụ. Người d&ugrave;ng c&oacute; thể bay đến những v&ugrave;ng kh&ocirc;ng quan xa xăm nhất giống như trong Earth mode vậy. Tiền th&acirc;n của Google Earth v&agrave; GEP l&agrave; một chương tr&igrave;nh mang t&ecirc;n Keyhole c&oacute; gi&aacute; $90. Khi Google mua lại Keyhole, họ được thừa hưởng h&agrave;ng terabyte dữ liệu bản đồ số v&agrave; bắt đầu tạo n&ecirc;n phi&ecirc;n bản đầu ti&ecirc;n của phần mềm miễn ph&iacute; n&agrave;y. C&aacute;c bản đồ ảnh tr&ecirc;n Google Earth chủ yếu đến từ hai nguồn: vệ tinh v&agrave; m&aacute;y bay, do c&aacute;c tổ chức như TeleAtlas v&agrave; EarthSat tổng hợp th&agrave;nh dạng số d&ugrave;ng cho c&aacute;c ứng dụng thương mai. Do dữ liệu của Google đến từ nhiều nguồn kh&aacute;c nhau, n&ecirc;n ch&uacute;ng c&oacute; độ ph&acirc;n giải kh&aacute;c nhau. Đ&oacute; l&agrave; l&yacute; do tại sao một số v&ugrave;ng tr&ecirc;n tr&aacute;i đất lại c&oacute; độ n&eacute;t rất cao ngay cả ở mức độ đường phố, c&ograve;n một số kh&aacute;c lại rất mờ d&ugrave; ở khoảng c&aacute;ch xa. Google đang cố gắng để to&agrave;n bộ h&igrave;nh ảnh về tr&aacute;i đất đều c&oacute; độ n&eacute;t mong muốn. Khi sử dụng Google Earth, bạn kh&ocirc;ng được xem trực hiện h&igrave;nh ảnh trong thời gian thực: theo Google, th&ocirc;ng tin bạn nhận được kh&ocirc;ng qu&aacute; ba năm, v&agrave; vẫn đang được cập nhật khi c&oacute; dữ liệu mới. Trong Google, một số quốc gia như Mỹ, Canada v&agrave; Anh c&oacute; độ n&eacute;t h&igrave;nh ảnh rất cao ở mức độ đường phố, v&igrave; thế bạn c&oacute; thể zoom v&agrave;o v&agrave; xem t&ecirc;n đường cũng như c&aacute;c cửa hiệu trong th&agrave;nh phố, v&agrave; t&igrave;m hướng đi từ nơi n&agrave;y đến nơi kh&aacute;c. Ngo&agrave;i ra cơ sở dữ liệu Google cũng c&oacute; một lượng lớn th&ocirc;ng tin về những khu vực kh&aacute;c như T&acirc;y &Acirc;u, Ấn Độ v&agrave; Nhật Bản, nhưng &ldquo;phần c&ograve;n lại của thế giới&rdquo; th&igrave; thường bị xao nh&atilde;ng. Mặc d&ugrave; bạn c&oacute; thể zoom v&agrave;o v&agrave; c&oacute; được h&igrave;nh ảnh rất r&otilde; n&eacute;t về c&aacute;c kim tự th&aacute;p Ai Cập, nhưng bạn kh&ocirc;ng thể xem được t&ecirc;n đường hoặc t&igrave;m được cửa h&agrave;ng b&aacute;n rau quả n&agrave;o trong th&agrave;nh phố. Tuy vậy, hiện tại Google đang li&ecirc;n tục bổ sung th&ecirc;m th&ocirc;ng tin cho cơ sở dữ liệu của m&igrave;nh, v&agrave; c&aacute;c tấm bản đồ đang ng&agrave;y c&agrave;ng trở n&ecirc;n chi tiết hơn. Điều n&agrave;y đưa ch&uacute;ng ta đến một nguồn kh&aacute;c của cơ sở dữ liệu Google Earth: cỗ m&aacute;y t&igrave;m kiếm Google. Một trong những điều l&agrave;m n&ecirc;n sức hấp dẫn của Google Earth ch&iacute;nh l&agrave; sự hợp t&aacute;c của n&oacute; với c&ocirc;ng cụ t&igrave;m kiếm Google. Như đ&atilde; đề cập ở tr&ecirc;n, khi xem một th&agrave;nh phố, bạn c&oacute; thể t&igrave;m c&aacute;c qu&aacute;n caf&eacute;, nh&agrave; h&agrave;ng, cửa h&agrave;ng rau quả, qu&aacute;n bar v&agrave; v&ocirc; số c&aacute;c cửa tiệm kh&aacute;c trong khu vực, sau đ&oacute; bạn c&oacute; thể click để t&igrave;m th&ocirc;ng tin chi tiết từ Google search. Người d&ugrave;ng cũng c&oacute; thể th&ecirc;m địa điểm v&agrave;o bản đồ bằng c&aacute;ch click &quot;Add/Edit a Business Listing&rdquo; trong thanh c&ocirc;ng cụ của Google Earth. Tuỳ biến Google Earth Pro Một trong số những yếu tố khiến Google Earth nổi bật hẳn so với c&aacute;c chương tr&igrave;nh bản đồ kh&aacute;c l&agrave; khả năng tuỳ biến m&agrave; n&oacute; đem lại cho người d&ugrave;ng. Bạn c&oacute; thể dễ d&agrave;ng th&ecirc;m v&agrave;o c&aacute;c địa điểm tr&ecirc;n tấm bản đồ m&agrave; bạn tạo ra, chỉ cần v&agrave;i thao t&aacute;c đơn giản với thanh c&ocirc;ng cụ. C&ograve;n nếu như vậy vẫn chưa l&agrave;m bạn vừa l&ograve;ng, bạn c&oacute; thể tự nhập file của m&igrave;nh v&agrave;o Google Earth để hiển thị đường đi, địa điểm, dữ liệu đường bi&ecirc;n&hellip; Cũng như c&aacute;ch tr&igrave;nh duyệt Internet đọc HTML, Google Earth cũng đọc một loại ng&ocirc;n ngữ c&oacute; t&ecirc;n KML. Bạn c&oacute; thể mở file KML của ri&ecirc;ng m&igrave;nh trong ứng dụng Google Earth Pro v&agrave; xem dữ liệu của m&igrave;nh hiển thị tr&ecirc;n bản đồ của Google Earth Pro. Điều n&agrave;y rất c&oacute; &iacute;ch trong việc điều tra ranh giới khu vực để th&agrave;nh lập bản đồ hiện trạng hay phục vụ rất lớn cho ng&agrave;nh đo đạc Một số lo ngại về Google Earth Google Earth gi&uacute;p bất kỳ ai c&oacute; m&aacute;y t&iacute;nh nối mạng cũng c&oacute; thể xem được h&igrave;nh ảnh chụp từ tr&ecirc;n kh&ocirc;ng của từng inch vu&ocirc;ng tr&ecirc;n mặt đất. V&igrave; thế, nhiều người đ&atilde; tỏ &yacute; lo ngại về vấn đề n&agrave;y. Tất cả mọi người đều c&oacute; phản ứng mạnh mẽ với Google Earth ngay từ lần đầu ti&ecirc;n sử dụng ứng dụng n&agrave;y. Phản ứng thường gặp l&agrave; sự kinh ngạc v&agrave; th&iacute;ch th&uacute; về khả năng tiếp cận thế giới ng&agrave;y c&agrave;ng mở rộng. Ngo&agrave;i ra c&ograve;n một phản ứng kh&aacute;c, thường diễn ra ngay sau phản ứng đầu ti&ecirc;n, li&ecirc;n quan đến sự kh&oacute; chịu của con người khi nh&igrave;n thấy địa chỉ nh&agrave; họ, &ldquo;Ồ, thật l&agrave; sởn t&oacute;c g&aacute;y.&rdquo; Trong khi con người thường th&iacute;ch quan s&aacute;t cả thế giới tr&ecirc;n m&aacute;y t&iacute;nh của m&igrave;nh, họ lại kh&ocirc;ng th&iacute;ch cả thế giới nh&igrave;n thấy họ. Liệu c&oacute; người d&ugrave;ng Google Earth n&agrave;o theo đuổi mục ti&ecirc;u thuận lợi hơn? Liệu bọn trộm c&oacute; lợi dụng Google Earth để do th&aacute;m khu vực n&agrave;o kh&ocirc;ng? C&oacute; một số vấn đề li&ecirc;n quan tới quyền ri&ecirc;ng tư c&aacute; nh&acirc;n cần ch&uacute; &yacute;. Đầu ti&ecirc;n, th&ocirc;ng tin về Google Earth c&oacute; mặt tr&ecirc;n to&agrave;n thế giới; v&agrave; thứ hai, c&aacute;c h&igrave;nh ảnh n&agrave;y c&oacute; thể l&ecirc;n đến 3 năm tuổi. Kh&ocirc;ng ai c&oacute; thể sử dụng ứng dụng n&agrave;y để xem ảnh vệ tinh thời gian thực được. R&otilde; r&agrave;ng c&aacute;c dữ liệu 3 năm tuổi kh&ocirc;ng c&oacute; &iacute;ch lắm đối với bọn trộm. Một số người kh&aacute;c lại lo rằng chương tr&igrave;nh n&agrave;y tập trung v&agrave;o an ninh quốc gia. C&aacute;c quan chức tại nhiều quốc gia đ&atilde; b&agrave;y tỏ lo ngại về mức độ chi tiết của th&ocirc;ng tin m&agrave; Google Earth cung cấp, trong đ&oacute; c&oacute; Australia, Anh v&agrave; Mỹ. Trong thực tế, c&aacute;c phần tử khủng bố c&oacute; thể lợi dụng chương tr&igrave;nh n&agrave;y để c&oacute; được những h&igrave;nh ảnh vệ tinh chi tiết về to&agrave;n thế giới. Nhưng chắc chắn quốc gia phản đối Google Earth dữ dội nhất cho đến giờ l&agrave; H&agrave;n Quốc. Hiện tại H&agrave;n Quốc vẫn đang trong t&igrave;nh trạng đối đầu với Bắc Triều Ti&ecirc;n, v&agrave; người d&acirc;n H&agrave;n Quốc hẳn rất phiền l&ograve;ng khi những tay s&uacute;ng Bắc Triều c&oacute; thể quan s&aacute;t c&aacute;c khu vực qu&acirc;n sự của H&agrave;n Quốc chỉ bằng một c&uacute; click chuột v&agrave; zoom v&agrave;o một ch&uacute;t. N&oacute;i một c&aacute;ch thực tế, nếu Google c&oacute; thể c&oacute; được những th&ocirc;ng tin n&agrave;y, th&igrave; nhiều khả năng Bắc Triều Ti&ecirc;n cũng c&oacute; được ch&uacute;ng rồi. Hơn nữa, bất kỳ ai tại H&agrave;n Quốc cũng c&oacute; thể zoom v&agrave;o một cơ sở nghi&ecirc;n cứu vũ kh&iacute; hạt nh&acirc;n của Bắc Triều Ti&ecirc;n. Google Earth biến việc do th&aacute;m trở th&agrave;nh tr&ograve; trẻ con. Những h&igrave;nh ảnh do Google Earth Pro cung cấp cũng kh&ocirc;ng c&oacute; g&igrave; mới &ndash; x&eacute;t cho c&ugrave;ng th&igrave; Google cũng thu thập dữ liệu từ c&aacute;c nguồn kh&aacute;c. Nhưng c&aacute;ch thức đ&oacute;ng g&oacute;i dữ liệu của họ th&igrave; thực sự l&agrave; một cuộc c&aacute;ch mạng. Bạn kh&ocirc;ng cần phải tổ chức một cuộc t&igrave;m kiếm chuy&ecirc;n s&acirc;u để lấy được một h&igrave;nh ảnh chụp từ vệ tinh của c&aacute;c kim tự th&aacute;p Ai Cập hay của Nh&agrave; trắng nữa. Tất cả những g&igrave; bạn cần l&agrave;m l&agrave; download Google Earth về m&aacute;y, v&agrave; bắt đầu quan s&aacute;t cả thế giới .</p>\r\n', '2018-05-21 15:47:48'),
(5, 4, 2, 'Mấy ứng dụng đọc sách điện tử nên dùng', '<p>Việc đọc s&aacute;ch điện tử tr&ecirc;n thiết bị SmartPhone (trừ iPhone, Window Phone) hiện nay c&oacute; thể n&oacute;i đ&atilde; trở n&ecirc;n tiện lợi hơn nhiều so với trước đ&acirc;y. Ngo&agrave;i SmartPhone c&ograve;n c&oacute; Tablet, Phablet... v&agrave; cả Smart TV nữa.<br />\r\nS&aacute;ch điện tử th&igrave; cũng rất nhiều v&agrave; cũng rất nhiễu l&agrave; miễn ph&iacute; hoặc được đăng tải kh&ocirc;ng c&oacute; bản quyền tr&ecirc;n Internet.<br />\r\nC&aacute;c phần mềm tạo s&aacute;ch điện tử miễn ph&iacute; c&oacute; nhiều v&agrave; tạo được s&aacute;ch với c&aacute;c định dạng phổ biến (mobi, ePub, PDF...), tương th&iacute;ch được với nhiều thiết bị, hệ điều h&agrave;nh; tạo được s&aacute;ch trực tiếp từ c&aacute;c trang web tr&ecirc;n Internet, hoặc từ c&aacute;c tệp tin văn bản đơn thuần cho đến c&aacute;c tệp tin của ứng dụng văn ph&ograve;ng: Word, Excel, PowerPoint...<br />\r\nV&agrave; phần mềm (ứng dụng) đọc s&aacute;ch cũng rất đa dạng, nhiều ứng dụng được cung cấp miễn ph&iacute;.<br />\r\nTuy nhi&ecirc;n, sử dụng phần mềm đọc s&aacute;ch điện tử n&agrave;o cho tiện lợi v&agrave; ph&ugrave; hợp với thiết bị th&igrave; chưa thấy c&oacute; một chỉ dẫn cụ thể n&agrave;o.<br />\r\n<br />\r\nB&agrave;i viết t&oacute;m lược 3 ứng dụng đọc s&aacute;ch điện tử miễn ph&iacute; c&oacute; nhiều t&iacute;nh năng hữu &iacute;ch hiện nay để mn tham khảo.<br />\r\n<br />\r\n<strong>I. Ứng dụng AlReader - any text book reader</strong>&nbsp;</p>\r\n\r\n<p>Phi&ecirc;n bản hiện tại: 1.871711070<br />\r\nĐiểm đ&aacute;nh gi&aacute; tr&ecirc;n GP: 4.6<br />\r\nNh&agrave; ph&aacute;t triển: Alan.Neverland [<a href=\"http://www.vn-zoom.com/register.php\" onclick=\"alert(\'You made the correct decision :)\')\"><input type=\"button\" value=\"Hãy đăng ký làm thành viên để xem được liên kết này\" /></a>&nbsp;]<br />\r\nĐịa chỉ cho PC tải về: [<a href=\"http://www.vn-zoom.com/register.php\" onclick=\"alert(\'You made the correct decision :)\')\"><input type=\"button\" value=\"Hãy đăng ký làm thành viên để xem được liên kết này\" /></a>&nbsp;]<br />\r\nDung lượng c&agrave;i đặt tr&ecirc;n thiết bị: 6,36 Mb<br />\r\n<br />\r\nChương tr&igrave;nh được thiết kế để đọc c&aacute;c s&aacute;ch điện tử chứa nội dung text l&agrave; chủ yếu.<br />\r\n- HĐH Android 1.6 +<br />\r\n- Đọc c&aacute;c định dạng: FB2, fb3, fbz, txt, epub (kh&ocirc;ng DRM), html, doc, docx, odt, rtf, mobi (kh&ocirc;ng DRM), prc (PalmDoc), tcr. ZIP Hỗ trợ v&agrave; t&agrave;i liệu lưu trữ GZ.<br />\r\n- Hỗ trợ Text-To-Speech (TTS) với chọn ng&ocirc;n ngữ tự động c&oacute; Việt ngữ, tạm dừng TTS khi c&oacute; cuộc gọi đến.<br />\r\n- Thư viện hỗ trợ mạng (OPDS).<br />\r\n- Thư viện tr&ecirc;n thiết bị. Lựa chọn theo t&aacute;c giả, loạt, ti&ecirc;u đề, năm xuất bản, thể loại.<br />\r\n- Ng&ocirc;n ngữ giao diện hỗ trợ: Nga, tiếng Anh, tiếng Đức, tiếng Hy Lạp, Ucraina, belarusian, Ba Lan, chineze, Bungary, Thổ Nhĩ Kỳ.<br />\r\n- Hỗ trợ từ điển b&ecirc;n ngo&agrave;i.<br />\r\n- Chỉnh sửa tập tin FB2 v&agrave; TXT.<br />\r\n- C&oacute; thể chọn trang m&atilde; cho t&ecirc;n tập tin trong zip.<br />\r\n- Bốn thiết lập sẵn với font chữ độc lập, m&agrave;u sắc, độ s&aacute;ng, chỉnh tương phản v&agrave; thụt lề của văn bản.<br />\r\n- Chế độ hai trang khi thiết bị xoay ngang.<br />\r\n- Cấu h&igrave;nh cho phong c&aacute;ch hiển thị: ti&ecirc;u đề, tr&iacute;ch dẫn, t&oacute;m tắt, vv... (mỗi phong c&aacute;ch hiển thị bao gồm font chữ, m&agrave;u sắc, kiểu chữ, cỡ chữ, đổ b&oacute;ng, thụt lề, khoảng c&aacute;ch...)<br />\r\n- Chức năng T&igrave;m kiếm.<br />\r\n- Di chuyển trong văn bản: theo phần trăm, theo trang, đến đầu / cuối văn bản, 10 trang về ph&iacute;a trước / sau, đến chương kế tiếp / trước.<br />\r\n- Hiển thị mục lục của fb2, doc v&agrave; epub.<br />\r\n- C&oacute; khả năng giữ đ&egrave;n m&agrave;n h&igrave;nh nền l&ecirc;n đến 20 ph&uacute;t khi kh&ocirc;ng hoạt động.<br />\r\n- Đồng bộ h&oacute;a vị tr&iacute; đọc qua mạng hoặc hệ thống tập tin.<br />\r\n<br />\r\n<strong>II. Ứng dụng eReader Prestigio: Book Reader</strong>&nbsp;</p>\r\n\r\n<p>Phi&ecirc;n bản hiện tại: 6.0.0.3<br />\r\nĐiểm đ&aacute;nh gi&aacute; tr&ecirc;n GP: 4.6<br />\r\nNh&agrave; ph&aacute;t triển: Prestigio [<a href=\"http://www.vn-zoom.com/register.php\" onclick=\"alert(\'You made the correct decision :)\')\"><input type=\"button\" value=\"Hãy đăng ký làm thành viên để xem được liên kết này\" /></a>&nbsp;]<br />\r\nĐịa chỉ cho PC tải về: [<a href=\"http://www.vn-zoom.com/register.php\" onclick=\"alert(\'You made the correct decision :)\')\"><input type=\"button\" value=\"Hãy đăng ký làm thành viên để xem được liên kết này\" /></a>&nbsp;]<br />\r\nDung lượng c&agrave;i đặt tr&ecirc;n thiết bị: 37,32 Mb<br />\r\n<br />\r\neReader Prestigio: Book Reader l&agrave; một ứng dụng đọc s&aacute;ch điện tử đa ng&ocirc;n ngữ, đa định dạng văn bản. Giao diện trực quan với hơn 25 ng&ocirc;n ngữ v&agrave; c&oacute; chức năng Text-To-Speech.<br />\r\n<br />\r\n☆ eReader Prestigio l&agrave; ứng dụng đọc s&aacute;ch chứa nội dung văn bản (Text), c&oacute; thể mở c&aacute;c tập tin văn bản trong nhiều định dạng bao gồm epub, html, FB2, fb2.zip, txt, pdf, mobi, epub3, djvu v&agrave; nhiều dạng văn bản kh&aacute;c; Audio Book định dạng MP3, AAC, M4B.<br />\r\n<br />\r\n☆ L&uacute;c mệt mỏi, bạn c&oacute; thể bật Text-To-Speech để ứng dụng đọc cuốn s&aacute;ch cho bạn nghe bằng giọng đọc du giường của chị Google! Những c&acirc;u chuyện cổ t&iacute;ch sẽ đưa những đứa trẻ của bạn đi v&agrave;o giấc mơ thiến thận... &agrave; nhầm... thi&ecirc;n thần.<br />\r\nV&agrave; c&ograve;n nhiều t&iacute;nh năng th&uacute; vị kh&aacute;c, mn tự kh&aacute;m ph&aacute;.<br />\r\n<br />\r\n<strong>III. Ứng dụng @Voice Aloud Reader</strong></p>\r\n\r\n<p>Phi&ecirc;n bản hiện tại: 12.06.01<br />\r\nĐiểm đ&aacute;nh gi&aacute; tr&ecirc;n GP: 4.4<br />\r\nNh&agrave; ph&aacute;t triển: Hyperionics Technology [<a href=\"http://www.vn-zoom.com/register.php\" onclick=\"alert(\'You made the correct decision :)\')\"><input type=\"button\" value=\"Hãy đăng ký làm thành viên để xem được liên kết này\" /></a>&nbsp;]<br />\r\nĐịa chỉ cho PC tải về: [<a href=\"http://www.vn-zoom.com/register.php\" onclick=\"alert(\'You made the correct decision :)\')\"><input type=\"button\" value=\"Hãy đăng ký làm thành viên để xem được liên kết này\" /></a>&nbsp;]<br />\r\nDung lượng c&agrave;i đặt tr&ecirc;n thiết bị: 38,04 Mb<br />\r\n<br />\r\nỨng dụng đọc to hoặc đọc c&aacute;c trang m&agrave;n h&igrave;nh web, tin tức, email d&agrave;i, TXT, PDF, DOC, DOCX, RTF, documens OpenOffice, EPUB, MOBI, PRC, AZW, ebooks FB2 v&agrave; nhiều hơn nữa. Đ&acirc;y l&agrave; một đầu đọc HTML, đọc t&agrave;i liệu v&agrave; đọc ebook tất cả trong một, cả cho việc đọc tr&ecirc;n m&agrave;n h&igrave;nh, hoặc nghe khi đ&ocirc;i mắt của bạn bị tổn thương, bị trục trặc hoặc đang bận rộn ở nơi kh&aacute;c.<br />\r\n<br />\r\nT&Iacute;NH NĂNG, ĐẶC ĐIỂM:<br />\r\n* Mở văn bản, PDF, DOC, DOCX, RTF, văn bản OpenOffice hoặc file HTML từ file hệ thống Android.<br />\r\n* Nhấn biểu tượng &quot;Chia sẻ&quot; trong bất kỳ ứng dụng n&agrave;o để gửi văn bản sang @Voice Aloud Reader đọc qua loa hoặc tai nghe.<br />\r\n* Khi đọc c&aacute;c trang web, c&aacute;c mục tr&igrave;nh đơn, chuyển hướng, quảng c&aacute;o, tin r&aacute;c kh&aacute;c được loại bỏ, c&ograve;n lại văn bản sạch để đọc hoặc nghe.<br />\r\n* Nếu ứng dụng kh&ocirc;ng c&oacute; sẵn biểu tượng &quot;Chia sẻ&quot;, c&oacute; thể sao ch&eacute;p văn bản trong ứng dụng đ&oacute; v&agrave; d&aacute;n n&oacute; v&agrave;o @Voice để đọc.<br />\r\n* Đọc ebook tr&ecirc;n m&agrave;n h&igrave;nh với sự t&ocirc; s&aacute;ng, chuyển c&aacute;c trang v&agrave; đọc li&ecirc;n tục. H&atilde;y gắn tai nghe, tắt m&agrave;n h&igrave;nh v&agrave; lắng nghe!<br />\r\n* Ghi lại nội dung th&agrave;nh file - WAV (kh&ocirc;ng n&eacute;n) hoặc (n&eacute;n), hỗ trợ định dạng OGG.<br />\r\n* Chỉnh Speech cho TTS, t&ugrave;y chọn sử dụng Regular Expressions (Regex)<br />\r\n* Desktop / m&aacute;y t&iacute;nh x&aacute;ch tay mở rộng tr&igrave;nh duyệt Google Chrome để th&ecirc;m b&agrave;i viết cho @Voice ch&egrave;n v&agrave;o danh s&aacute;ch đọc với một c&uacute; nhấp chuột.<br />\r\n* Dễ d&agrave;ng điều khiển &acirc;m lượng, s&acirc;n v&agrave; tốc độ của lời n&oacute;i.<br />\r\n* Tự động nhận ra ng&ocirc;n ngữ để sử dụng giọng n&oacute;i TTS th&iacute;ch hợp, nếu c&oacute;, hoặc thiết lập tiếng n&oacute;i ưa th&iacute;ch của bạn bằng tay với mục tr&igrave;nh đơn: &quot;Chọn ng&ocirc;n ngữ&quot;.<br />\r\n* T&ugrave;y chọn hẹn giờ ngủ để dừng đọc sau một thời gian nhất định.<br />\r\n<br />\r\nT&oacute;m lại:<br />\r\nCả 3 ứng dụng đều n&ecirc;n d&ugrave;ng, mỗi ứng dụng c&oacute; ưu thế ri&ecirc;ng v&agrave; bổ sung cho nhau.<br />\r\nVới thiết bị c&oacute; dung lượng nhỏ th&igrave; n&ecirc;n d&ugrave;ng Alreader, tuy c&oacute; hạn chế về định dạng s&aacute;ch điện tử, hoặc d&ugrave;ng @Voice Aloud Reader.<br />\r\nSử dụng th&ecirc;m ứng dụng vnSpeak TTS để bổ sung giọng đọc người thật. Tuy nhi&ecirc;n đối với m&igrave;nh th&igrave;:<br />\r\n&nbsp;</p>\r\n\r\n<p>Lỡ y&ecirc;u Gụ gỗ (Google) mất rồi,<br />\r\nHồng Vi m&atilde;i chỉ l&agrave; người đến sau.</p>\r\n\r\n<p><br />\r\nvnSpeak TTS: Bộ đọc tiếng Việt</p>\r\n\r\n<p>hi&ecirc;n bản hiện tại: 3.5<br />\r\nĐiểm đ&aacute;nh gi&aacute; tr&ecirc;n GP: 4.2<br />\r\nĐịa chỉ cho PC tải về: [<a href=\"http://www.vn-zoom.com/register.php\" onclick=\"alert(\'You made the correct decision :)\')\"><input type=\"button\" value=\"Hãy đăng ký làm thành viên để xem được liên kết này\" /></a>&nbsp;]<br />\r\nDung lượng c&agrave;i đặt tr&ecirc;n thiết bị: 48,67 Mb</p>\r\n', '2018-05-21 15:49:34');
INSERT INTO `threads` (`id`, `topic_id`, `user_id`, `title`, `content`, `date_created`) VALUES
(6, 4, 2, 'Những tính năng hữu ích của Revo Uninstaller Pro', '<p>Khi nhắc đến c&aacute;i t&ecirc;n&nbsp;<strong>Revo Uninstaller</strong>, bạn đọc sẽ biết ngay đ&acirc;y l&agrave; c&ocirc;ng cụ hỗ trợ gỡ bỏ c&aacute;c chương tr&igrave;nh, ứng dụng kh&ocirc;ng cần thiết ra khỏi m&aacute;y t&iacute;nh. Nhưng&nbsp;<strong>Revo Uninstaller Pro</strong>&nbsp;c&oacute; những chức năng hữu dụng n&agrave;o th&igrave; rất &iacute;t người d&ugrave;ng biết đến bởi chưa bao giờ t&igrave;m hiểu hoặc kh&ocirc;ng biết đến sự c&oacute; mặt của n&oacute; trong cơ cấu hoạt động của c&ocirc;ng cụ m&agrave; nh&agrave; ph&aacute;t triển đ&atilde; cung cấp, t&iacute;ch hợp sẵn. B&agrave;i viết sẽ ph&acirc;n t&iacute;ch đ&ocirc;i ch&uacute;t về hoạt động của ch&uacute;ng&hellip;<br />\r\n<br />\r\nTại giao diện của&nbsp;<strong>Revo Uninstaller Pro</strong>&nbsp;bao gồm c&aacute;c thẻ ch&iacute;nh như&nbsp;</p>\r\n\r\n<p><strong>1. File</strong><br />\r\n<br />\r\n<strong>* Install Program:</strong>&nbsp;Hỗ trợ người d&ugrave;ng quản l&yacute; tốt hơn th&ocirc;ng tin c&aacute;c tr&igrave;nh đăng k&yacute; tr&ecirc;n hệ thống của phần mềm muốn c&agrave;i đặt, bằng c&aacute;ch theo d&otilde;i qu&aacute; tr&igrave;nh c&agrave;i đặt của phần mềm v&agrave; sau đ&oacute; lưu lại với t&ecirc;n do ch&iacute;nh người sử dụng đặt theo nhu cầu c&aacute; nh&acirc;n để tiện cho việc truy xuất nhằm hỗ trợ cho việc gỡ bỏ khi phần mềm đ&oacute; kh&ocirc;ng c&ograve;n ph&ugrave; hợp hoặc bị lỗi kỹ thuật. Để k&iacute;ch hoạt bạn đọc khởi động&nbsp;<strong>Revo Unintaller Pro</strong>&nbsp;sau đ&oacute; nhấn tổ hợp ph&iacute;m&nbsp;<strong>Ctrl + L</strong>&nbsp;&gt; Cửa sổ Windows Explorer hiển thị t&igrave;m tới vị tr&iacute; lưu trữ tập tin c&agrave;i đặt phần mềm muốn sử dụng. L&uacute;c n&agrave;y tr&ecirc;n m&agrave;n h&igrave;nh sẽ hiển thị thanh Appbar nhằm gi&aacute;m s&aacute;t, thu thập to&agrave;n bộ th&ocirc;ng tin sẽ được đăng k&yacute; tr&ecirc;n hệ thống của phần mềm được c&agrave;i đặt.<br />\r\n<br />\r\n<strong>Lưu &yacute;:</strong>&nbsp;AE th&agrave;nh vi&ecirc;n, bạn đọc y&ecirc;u c&ocirc;ng nghệ h&atilde;y lưu lại to&agrave;n bộ c&aacute;c c&ocirc;ng việc đang thực hiện v&agrave; tắt to&agrave;n bộ những chương tr&igrave;nh, ứng dụng đang chạy trước khi thực hiện để tạo một bản ghi (Log) kh&ocirc;ng chứa r&aacute;c thừa từ hoạt động của c&aacute;c ứng dụng kh&aacute;c nhằm hỗ trợ cho qu&aacute; tr&igrave;nh gỡ bỏ sau n&agrave;y chuẩn x&aacute;c hơn.<br />\r\n<br />\r\n<strong>* Rebuild cache file:</strong>&nbsp;Khi nhấn chọn lập tức c&aacute;c thiết lập c&aacute; nh&acirc;n sẽ bị huỷ để trở về những thiết lập mặc định ban đầu.<br />\r\n<br />\r\n<strong>* Export to:</strong>&nbsp;Tạo bản b&aacute;o c&aacute;o theo định dạng văn bản .TXT, HTML hoặc Excel<br />\r\n<br />\r\n_ C&aacute;c chức năng Print th&igrave; AE th&agrave;nh vi&ecirc;n, bạn đọc đ&atilde; qu&aacute; hiểu để l&agrave;m g&igrave; rồi n&ecirc;n b&agrave;i viết sẽ bỏ qua kh&ocirc;ng đề cập đến.</p>\r\n\r\n<p><strong>2. View:</strong>&nbsp;Khu vực liệt k&ecirc; c&aacute;c thanh c&ocirc;ng cụ cần thiết để AE th&agrave;nh vi&ecirc;n, bạn đọc k&iacute;ch hoạt nhằm hiển thị tr&ecirc;n giao diện ch&iacute;nh của&nbsp;<strong>Revo Uninstaller Pro</strong>&nbsp;hoặc tắt bỏ sao cho tiện dụng nhất trong l&uacute;c sử dụng. Bạn đọc h&atilde;y để giao diện c&agrave;ng đơn giản bao nhi&ecirc;u c&agrave;ng dễ thao t&aacute;c hơn v&igrave; n&oacute; tr&aacute;nh cho người d&ugrave;ng chọn nhầm c&aacute;c t&iacute;nh năng do lỡ tay trong l&uacute;c gỡ bỏ khiến qu&aacute; tr&igrave;nh bị ngưng đột ngột dẫn đến lỗi l&agrave;m ảnh hưởng đến hiệu suất hoạt động của Windows.<br />\r\n<br />\r\n<strong>* Hunter Mode:</strong>&nbsp;L&agrave; chế độ hiển thị thay thế cho biểu tượng của&nbsp;<strong>Revo Unistaller Pro</strong>&nbsp;ngo&agrave;i m&agrave;n h&igrave;nh nhằm tr&aacute;nh tạo th&ecirc;m sự hổn độn vốn c&oacute; do m&aacute;y đ&atilde; được c&agrave;i đặt qu&aacute; nhiều c&aacute;c chương tr&igrave;nh, ứng dụng phục vụ cho c&ocirc;ng việc h&agrave;ng ng&agrave;y.&nbsp;<strong>Hunter Mode</strong>&nbsp;gi&uacute;p người sử dụng truy cập nhanh v&agrave;o giao diện của&nbsp;<strong>Revo Uninstaller Pro</strong>. Ngo&agrave;i ra, người d&ugrave;ng c&oacute; thể thay đổi k&iacute;ch thước của biểu tượng (Windows size), hoặc cho ph&eacute;p khởi động c&ugrave;ng Windows v&agrave; tinh chỉnh độ trong suốt (Transparency) sao cho ph&ugrave; hợp, kh&ocirc;ng l&agrave;m ảnh hưởng đến c&ocirc;ng việc.&nbsp;<br />\r\n<br />\r\n<strong>3. Modules</strong><br />\r\n<br />\r\n<strong>* Traced Program:</strong>&nbsp;Sẽ hiển thị c&aacute;c chương tr&igrave;nh, ứng dụng đ&atilde; được c&agrave;i đặt th&ocirc;ng qua chức năng&nbsp;<strong>Install Program</strong>&nbsp;v&agrave; để sử dụng bạn đọc chỉ cần nhấn chuột phải v&agrave;o ứng dụng muốn c&agrave;i đặt chọn &ldquo;<strong>Install with Revo Uninstaller Pro</strong>&rdquo; hoặc thực hiện thao t&aacute;c như đ&atilde; hướng dẫn b&ecirc;n tr&ecirc;n.</p>\r\n\r\n<p><strong>* Logs Database:</strong>&nbsp;L&agrave; nơi chứa dữ liệu, th&ocirc;ng tin của hơn&nbsp;<strong>16k</strong>&nbsp;c&aacute;c chương tr&igrave;nh, ứng dụng đ&atilde; được&nbsp;<strong>Revo Uninstaller Pro</strong>&nbsp;thu thập từ người d&ugrave;ng tr&ecirc;n to&agrave;n cầu để bạn đọc c&oacute; thể tải về m&aacute;y t&iacute;nh nhằm hỗ trợ cho qu&aacute; tr&igrave;nh gỡ bỏ được ch&iacute;nh x&aacute;c hơn, sạch sẽ hơn. Để sử dụng dữ liệu đang được chứa tr&ecirc;n m&aacute;y chủ của&nbsp;<strong>Revo Uninstaller Pro</strong>, bạn đọc h&atilde;y chọn ứng dụng th&iacute;ch hợp v&agrave; nhấn n&uacute;t&nbsp;<strong>Uninstall</strong>, qu&aacute; tr&igrave;nh sẽ tự động thực hiện. Tr&ecirc;n m&aacute;y đang sử dụng Revo Uninstaller Pro ph&aacute;t hiện c&oacute; 1.148 c&aacute;c tập tin đăng k&yacute; tương tự những dữ liệu đ&atilde; thu thập từ người d&ugrave;ng tr&ecirc;n to&agrave;n cầu.</p>\r\n\r\n<p>Nếu&nbsp;<strong>Revo Uninstaller Pro</strong>&nbsp;ph&aacute;t hiện kh&ocirc;ng c&oacute; c&aacute;c tr&igrave;nh đăng k&yacute; của ứng dụng tr&ecirc;n hệ thống sẽ hiển thị hộp thoại&nbsp;<strong>Advanced Uninstall</strong>&nbsp;với nội dung &ldquo;<strong>Những dữ liệu li&ecirc;n quan đến việc gỡ bỏ chương tr&igrave;nh, nhưng kh&ocirc;ng được ph&aacute;t hiện bởi v&igrave; n&oacute; kh&ocirc;ng được ghi lại từ Revo Uninstaller Pro trong qu&aacute; tr&igrave;nh c&agrave;i đặt ứng dụng. Nếu bạn muốn kiểm tra v&agrave; xo&aacute; bỏ những tập tin cuối c&ugrave;ng h&atilde;y nhấn n&uacute;t Advanced Uninstall</strong>&rdquo; v&agrave; biện ph&aacute;p tốt nhất l&agrave; h&atilde;y nhấn n&uacute;t&nbsp;<strong>Cancel</strong>&nbsp;hoắc nhấn&nbsp;<strong>X</strong>&nbsp;để huỷ bỏ lệnh thực hiện.</p>\r\n\r\n<p><strong>Lưu &yacute;:</strong><span style=\"font-size:13px\"><span style=\"color:#333333\"><span style=\"font-family:Verdana,Arial,Tahoma,Calibri,Geneva,sans-serif\"><span style=\"background-color:#ffffff\">&nbsp;Sau khi nhấn n&uacute;t&nbsp;</span></span></span></span><strong>Advanced Uninstall</strong><span style=\"font-size:13px\"><span style=\"color:#333333\"><span style=\"font-family:Verdana,Arial,Tahoma,Calibri,Geneva,sans-serif\"><span style=\"background-color:#ffffff\">, c&ocirc;ng cụ sẽ hiển thị danh s&aacute;ch c&aacute;c tập tin đăng k&yacute; tương tự như c&aacute;c tập tin đăng k&yacute; của ứng dụng đ&atilde; chọn trong danh s&aacute;ch&nbsp;</span></span></span></span><strong>Logs Database</strong><span style=\"font-size:13px\"><span style=\"color:#333333\"><span style=\"font-family:Verdana,Arial,Tahoma,Calibri,Geneva,sans-serif\"><span style=\"background-color:#ffffff\">,&nbsp;</span></span></span></span><strong><span style=\"color:#ff0000\"><span style=\"font-size:medium\">v&agrave; đ&acirc;y l&agrave; việc l&agrave;m rất nguy hiểm c&oacute; thể l&agrave;m tổn hại đến hệ điều h&agrave;nh dẫn đến việc kh&ocirc;ng thể truy cập Windows, n&ecirc;n bạn đọc tốt nhất kh&ocirc;ng n&ecirc;n t&ograve; m&ograve; kh&aacute;m ph&aacute; để tr&aacute;nh những t&igrave;nh huống dở kh&oacute;c dở cười kh&ocirc;ng mong muốn</span></span></strong><span style=\"font-size:13px\"><span style=\"color:#333333\"><span style=\"font-family:Verdana,Arial,Tahoma,Calibri,Geneva,sans-serif\"><span style=\"background-color:#ffffff\">.</span></span></span></span></p>\r\n\r\n<p><strong>4. Commands:</strong>&nbsp;bao gồm c&aacute;c lệnh thực thi c&oacute; thể hoạt động ngay tức th&igrave; l&agrave;&nbsp;<br />\r\n<br />\r\n<strong>_ Refresh:</strong>&nbsp;l&agrave;m mới giao diện của Revo Uninstaller Pro<br />\r\n<br />\r\n<strong>_ Add to Group:</strong>&nbsp;Cung cấp 4 nh&oacute;m mặc định l&agrave; Internet, Games, Multimedia, Utilities để người d&ugrave;ng c&oacute; thể sắp xếp tạo nh&oacute;m cho c&aacute;c chương tr&igrave;nh, ứng dụng đ&atilde; c&agrave;i đặt cho dễ quản l&yacute; v&agrave; c&oacute; thể tạo th&ecirc;m c&aacute;c nh&oacute;m mới tuỳ theo nhu cầu sử dụng c&aacute; nh&acirc;n.<br />\r\n<br />\r\n<strong>_ Forced Uninstall:</strong>&nbsp;Hỗ trợ gỡ bỏ cưởng &eacute;p với những ứng dụng cứng đầu kh&ocirc;ng mong muốn ra khỏi hệ thống.<br />\r\n<br />\r\nC&aacute;c t&iacute;nh năng Uninstall, Quick Uninstall, Add Comment, Change icon, Remove Group chỉ hiển thị khi người d&ugrave;ng nhấn phải chuột l&ecirc;n ứng dụng.&nbsp;</p>\r\n\r\n<p><strong>5. Tools:</strong>&nbsp;Cung cấp c&aacute;c c&ocirc;ng cụ hỗ trợ ch&iacute;nh của Revo Unistaller Pro<br />\r\n<br />\r\n<strong>* Uninstaller:</strong>&nbsp;Hỗ trợ gỡ bỏ c&aacute;c chương tr&igrave;nh, ứng dụng ra khỏi hệ thống nhanh ch&oacute;ng v&agrave; an to&agrave;n.<br />\r\n<strong>* Autorun Manager:</strong>&nbsp;Hỗ trợ quản l&yacute; c&aacute;c chương tr&igrave;nh, ứng dụng khởi động c&ugrave;ng Windows một c&aacute;ch hiệu quả bằng c&aacute;ch v&ocirc; hiệu ho&aacute; hoạt động của ch&uacute;ng gi&uacute;p qu&aacute; tr&igrave;nh khởi động của Windows nhanh hơn v&agrave; lấy lại t&agrave;i nguy&ecirc;n đang bị chiếm dụng bất hợp l&yacute;.<br />\r\n<strong>* Junk Files Cleaner:</strong>&nbsp;Hỗ trợ qu&eacute;t kiểm tra ổ đĩa nhằm ph&aacute;t hiện v&agrave; loại bỏ c&aacute;c tập tin r&aacute;c, lưu tạm trong bộ nhớ đệm, c&aacute;c tập tin đăng k&yacute; hỏng sau những đợt gỡ bỏ kh&ocirc;ng đ&uacute;ng qui tr&igrave;nh.<br />\r\n<strong>* Windows Tools:</strong>&nbsp;Cho ph&eacute;p người d&ugrave;ng c&oacute; thể truy cập đến c&aacute;c c&ocirc;ng cụ hỗ trợ ch&iacute;nh của Windows đ&atilde; được t&iacute;ch hợp sẵn trong cơ cấu hoạt động của HDH m&agrave; kh&ocirc;ng cần phải thực hiện bất kỳ một lệnh thực thi n&agrave;o với v&agrave;i c&aacute;i nhấp chuột đơn giản.<br />\r\n<strong>* Browsers Cleaner:</strong>&nbsp;Xo&aacute; lịch sử lướt web của tr&igrave;nh duyệt nhằm tr&aacute;nh những h&agrave;nh vi theo d&otilde;i th&oacute;i quen sử dụng internet của bạn đọc.<br />\r\n<strong>* Microsoft Office Cleaner:</strong>&nbsp;Xo&aacute; bỏ c&aacute;c dấu t&iacute;ch l&agrave;m việc với bộ ứng dụng văn ph&ograve;ng của Microsoft Office.<br />\r\n<strong>* Windows Cleaner:</strong>&nbsp;Hỗ trợ xo&aacute; bỏ c&aacute;c thao t&aacute;c thực hiện tr&ecirc;n m&aacute;y t&iacute;nh, b&agrave;n ph&iacute;m, xo&aacute; bỏ c&aacute;c tập tin đang lưu trữ tại khu vực th&ugrave;ng r&aacute;c, xo&aacute; lịch sử khởi động c&aacute;c ứng dụng từ danh s&aacute;ch Start Menu, xo&aacute; bỏ lịch sử t&igrave;m kiếm v.v&hellip;<br />\r\n<strong>* Evidence Remover:</strong>&nbsp;Hỗ trợ xo&aacute; bỏ c&aacute;c dữ liệu chứa tr&ecirc;n ph&acirc;n v&ugrave;ng với thuật to&aacute;n đặc biệt để sau khi thực hiện c&aacute;c tập tin, dữ liệu sẽ kh&ocirc;ng thể kh&ocirc;i phục lại được nữa. Nếu muốn l&agrave;m mới ổ đĩa, ph&acirc;n v&ugrave;ng h&atilde;y thực hiện thao t&aacute;c n&agrave;y sau đ&oacute; thực hiện tiếp format l&agrave; c&oacute; thể y&ecirc;n t&acirc;m rồi đấy.<br />\r\n<strong>* Unrecoverable Delete:</strong>&nbsp;Cho ph&eacute;p người d&ugrave;ng xo&aacute; bỏ c&aacute;c dữ liệu, thư mục, c&aacute;c tập tin khởi động một c&aacute;ch an to&agrave;n v&agrave; kh&ocirc;ng thể kh&ocirc;i phục m&agrave; kh&ocirc;ng hề l&agrave;m ảnh hưởng đến bề mặt hoạt động vật l&yacute; của ổ cứng.<br />\r\n<strong>* Backup Manager:</strong>&nbsp;Lưu giữ c&aacute;c qu&aacute; tr&igrave;nh đ&atilde; được gỡ bỏ c&aacute;c ứng dụng c&oacute; chủ đ&iacute;ch kết hợp bản ghi (Log) để c&oacute; thể dễ d&agrave;nh kh&ocirc;i phục lại khi hệ thống xảy ra những sự cố kh&ocirc;ng mong muốn sau khi gỡ bỏ ho&agrave;n tất.<br />\r\n<strong>* Options:</strong>&nbsp;Cho ph&eacute;p người d&ugrave;ng thiết lập tuỳ chỉnh c&aacute; nh&acirc;n với c&aacute;c chức năng ch&iacute;nh của&nbsp;<strong>Revo Uninstaller Pro</strong>&nbsp;hoặc thay đổi giao diện, ng&ocirc;n ngữ v.v&hellip;</p>\r\n\r\n<p><br />\r\n<strong>6. Revo AppBar</strong><br />\r\n<br />\r\nĐ&aacute;ng l&yacute; nội dung c&oacute; thể dừng lại nhưng do chức năng&nbsp;<strong>Install Programs</strong>&nbsp;vẫn c&ograve;n một c&aacute;i t&ecirc;n kh&aacute;c được t&iacute;ch hợp sẵn trong cấu tr&uacute;c c&agrave;i đặt của&nbsp;<strong>Revo Unistaller Pro</strong>, thanh c&ocirc;ng cụ n&agrave;y hoạt động ri&ecirc;ng lẻ b&ecirc;n ngo&agrave;i chi khi n&agrave;o k&iacute;ch hoạt n&oacute; mới xuất hiện tr&ecirc;n m&agrave;n h&igrave;nh được gọi l&agrave;&nbsp;<strong>Revo AppBar</strong>. Thanh c&ocirc;ng cụ sẽ gi&uacute;p người d&ugrave;ng thu thập tất cả c&aacute;c tr&igrave;nh đăng k&yacute; tr&ecirc;n hệ thống khi thực hiện c&agrave;i đặt một ứng dụng mới nhằm đ&aacute;p ứng cho nhu cầu sử dụng.</p>\r\n\r\n<p><strong>Lưu &yacute;:</strong>&nbsp;Qu&aacute; tr&igrave;nh c&agrave;i đặt kết th&uacute;c, bạn đọc đừng vội tắt&nbsp;<strong>Revo AppBar</strong>&nbsp;m&agrave; h&atilde;y thiết lập c&aacute;c cấu h&igrave;nh c&aacute; nh&acirc;n, k&iacute;ch hoạt bản quyền hoặc bẻ kho&aacute;. Sau khi đ&atilde; đồng &yacute; với những thiết lập c&aacute; nh&acirc;n v&agrave; k&iacute;ch hoạt bản quyền th&agrave;nh c&ocirc;ng h&atilde;y tho&aacute;t khỏi giao diện của chương tr&igrave;nh, ứng dụng vừa c&agrave;i đặt để tiếp tục l&agrave;m việc với&nbsp;<strong>Revo AppBar</strong>&nbsp;bằng c&aacute;ch chọn n&uacute;t&nbsp;<strong>Stop</strong>&nbsp;&gt; Lưu tập tin với t&ecirc;n bất kỳ sao cho dễ nhớ để tiện sử dụng cho sau n&agrave;y khi thực hiện gỡ bỏ, cũng như sao lưu ch&uacute;ng phục vụ cho việc tạo c&aacute;c bản Portable. V&agrave; b&ecirc;n dưới ch&iacute;nh l&agrave; đường dẫn vị tr&iacute; lưu c&aacute;c bản ghi (Logs) đ&atilde; được thực hiện đ&uacute;ng qui tr&igrave;nh với&nbsp;<strong>Revo AppBar</strong>.<br />\r\n<br />\r\n<strong>C:\\User\\User Name\\AppData\\Local(Temp)\\VS Revo Group\\Revo Uninstaller Pro\\Logs\\Filename by Your sight</strong><br />\r\n<br />\r\nTập tin b&ecirc;n trong bao gồm thư mục&nbsp;<strong>data.lhi</strong>;&nbsp;<strong>filelogs.dat</strong>;&nbsp;<strong>reglogs.dat</strong>&nbsp;v&agrave; c&oacute; thể nhiều hơn nữa tuỳ theo c&aacute;c tr&igrave;nh đăng k&yacute; của phần mềm c&agrave;i đặt sẽ thực hiện tr&ecirc;n hệ thống.<br />\r\nB&acirc;y giờ người d&ugrave;ng c&oacute; thể thu gom n&oacute; lưu v&agrave;o một vị tr&iacute; kh&aacute;c để nghi&ecirc;n cứu v&agrave; tạo c&aacute;c bản portable một c&aacute;ch dễ d&agrave;ng với c&aacute;c c&ocirc;ng cụ chuy&ecirc;n dụng hoặc thủ c&ocirc;ng qua&nbsp;<strong>Winrar</strong>&nbsp;chẳng hạn. Vấn đề l&agrave;m portable l&agrave; qu&aacute; mới mẽ v&agrave; đang t&igrave;m hiểu n&ecirc;n kh&ocirc;ng d&aacute;m lạm b&agrave;n m&agrave; chỉ gợi &yacute; để bạn đọc đ&atilde; thực hiện quen với c&ocirc;ng việc t&igrave;m hiểu th&ecirc;m về chức năng hữu &iacute;ch n&agrave;y của&nbsp;<strong>Revo Uninstaller Pro</strong>.<br />\r\n<br />\r\nQu&aacute; tr&igrave;nh hoạt động ngầm trong hệ thống, v&agrave; gỡ bỏ c&aacute;c c&ocirc;ng cụ của&nbsp;<strong>Revo Uninstaller Pro</strong>&nbsp;chiếm dụng t&agrave;i nguy&ecirc;n l&agrave; kh&ocirc;ng đ&aacute;ng kể v&igrave; thế bạn đọc c&oacute; thể y&ecirc;n t&acirc;m sử dụng. R<strong>evo Uninstaller</strong>&nbsp;c&oacute; hai phi&ecirc;n bản thương mại v&agrave; miễn ph&iacute;, cả hai đều l&agrave; c&ocirc;ng cụ hỗ trợ gỡ bỏ c&aacute;c chương tr&igrave;nh, ứng dụng ra khỏi hệ thống an to&agrave;n v&agrave; sạch sẽ nhất kể cả chương tr&igrave;nh như&nbsp;<strong>Microsoft Office</strong>&nbsp;hoặc&nbsp;<strong>Adobe Photoshop</strong>&nbsp;đều đ&atilde; được thực hiện một c&aacute;ch nhanh ch&oacute;ng, quan trọng hơn l&agrave; kh&ocirc;ng hề g&acirc;y ra lỗi kỹ thuật l&agrave;m ảnh hưởng đến hoạt động của Windows.&nbsp;<br />\r\n<br />\r\nNếu đang sử dụng với&nbsp;<strong>HDH 32bit</strong>, th&igrave; AE th&agrave;nh vi&ecirc;n chỉ cần tải&nbsp;<strong>Revo Uninstaller Free</strong>&nbsp;về sử dụng l&agrave; đủ sức để thực hiện gỡ bỏ c&aacute;c chương tr&igrave;nh ra khỏi hệ thống. Phi&ecirc;n bản Free kh&ocirc;ng hỗ trợ&nbsp;<strong>HDH 64bit</strong>. Nếu muốn trải nghiệm với&nbsp;<strong>Revo Uninsaller Pro</strong>&nbsp;h&atilde;y truy cập v&agrave;o subbox Phần Mềm Hệ Thống &amp; Bảo Mật t&igrave;m đến c&aacute;c topic được AE th&agrave;nh vi&ecirc;n chia sẻ tại đ&acirc;y hoặc tiện lợi hơn l&agrave; tải phi&ecirc;n bản x&aacute;ch tay (Portable) về lưu tr&ecirc;n USB để sử dụng mọi l&uacute;c, mọi nơi.</p>\r\n', '2018-05-21 15:51:42'),
(7, 4, 2, 'Mấy ứng dụng đọc sách điện tử nên dùng', '<p>Việc đọc s&aacute;ch điện tử tr&ecirc;n thiết bị SmartPhone (trừ iPhone, Window Phone) hiện nay c&oacute; thể n&oacute;i đ&atilde; trở n&ecirc;n tiện lợi hơn nhiều so với trước đ&acirc;y. Ngo&agrave;i SmartPhone c&ograve;n c&oacute; Tablet, Phablet... v&agrave; cả Smart TV nữa.<br />\r\nS&aacute;ch điện tử th&igrave; cũng rất nhiều v&agrave; cũng rất nhiễu l&agrave; miễn ph&iacute; hoặc được đăng tải kh&ocirc;ng c&oacute; bản quyền tr&ecirc;n Internet.<br />\r\nC&aacute;c phần mềm tạo s&aacute;ch điện tử miễn ph&iacute; c&oacute; nhiều v&agrave; tạo được s&aacute;ch với c&aacute;c định dạng phổ biến (mobi, ePub, PDF...), tương th&iacute;ch được với nhiều thiết bị, hệ điều h&agrave;nh; tạo được s&aacute;ch trực tiếp từ c&aacute;c trang web tr&ecirc;n Internet, hoặc từ c&aacute;c tệp tin văn bản đơn thuần cho đến c&aacute;c tệp tin của ứng dụng văn ph&ograve;ng: Word, Excel, PowerPoint...<br />\r\nV&agrave; phần mềm (ứng dụng) đọc s&aacute;ch cũng rất đa dạng, nhiều ứng dụng được cung cấp miễn ph&iacute;.<br />\r\nTuy nhi&ecirc;n, sử dụng phần mềm đọc s&aacute;ch điện tử n&agrave;o cho tiện lợi v&agrave; ph&ugrave; hợp với thiết bị th&igrave; chưa thấy c&oacute; một chỉ dẫn cụ thể n&agrave;o.<br />\r\n<br />\r\nB&agrave;i viết t&oacute;m lược 3 ứng dụng đọc s&aacute;ch điện tử miễn ph&iacute; c&oacute; nhiều t&iacute;nh năng hữu &iacute;ch hiện nay để mn tham khảo.<br />\r\n<br />\r\n<strong>I. Ứng dụng AlReader - any text book reader</strong>&nbsp;[<a href=\"http://www.vn-zoom.com/register.php\" onclick=\"alert(\'You made the correct decision :)\')\"><input type=\"button\" value=\"Hãy đăng ký làm thành viên để xem được liên kết này\" /></a>&nbsp;]<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://lh3.ggpht.com/RsR63rfgCqtLHLmWOYE9bUuHy-FBPc2CzkNal4vbj0aKiFkhB1Ov_sV8dM372KPlaJQ=w300\" /></p>\r\n\r\n<p><br />\r\nPhi&ecirc;n bản hiện tại: 1.871711070<br />\r\nĐiểm đ&aacute;nh gi&aacute; tr&ecirc;n GP: 4.6<br />\r\nNh&agrave; ph&aacute;t triển: Alan.Neverland [<a href=\"http://www.vn-zoom.com/register.php\" onclick=\"alert(\'You made the correct decision :)\')\"><input type=\"button\" value=\"Hãy đăng ký làm thành viên để xem được liên kết này\" /></a>&nbsp;]<br />\r\nĐịa chỉ cho PC tải về: [<a href=\"http://www.vn-zoom.com/register.php\" onclick=\"alert(\'You made the correct decision :)\')\"><input type=\"button\" value=\"Hãy đăng ký làm thành viên để xem được liên kết này\" /></a>&nbsp;]<br />\r\nDung lượng c&agrave;i đặt tr&ecirc;n thiết bị: 6,36 Mb<br />\r\n<br />\r\nChương tr&igrave;nh được thiết kế để đọc c&aacute;c s&aacute;ch điện tử chứa nội dung text l&agrave; chủ yếu.<br />\r\n- HĐH Android 1.6 +<br />\r\n- Đọc c&aacute;c định dạng: FB2, fb3, fbz, txt, epub (kh&ocirc;ng DRM), html, doc, docx, odt, rtf, mobi (kh&ocirc;ng DRM), prc (PalmDoc), tcr. ZIP Hỗ trợ v&agrave; t&agrave;i liệu lưu trữ GZ.<br />\r\n- Hỗ trợ Text-To-Speech (TTS) với chọn ng&ocirc;n ngữ tự động c&oacute; Việt ngữ, tạm dừng TTS khi c&oacute; cuộc gọi đến.<br />\r\n- Thư viện hỗ trợ mạng (OPDS).<br />\r\n- Thư viện tr&ecirc;n thiết bị. Lựa chọn theo t&aacute;c giả, loạt, ti&ecirc;u đề, năm xuất bản, thể loại.<br />\r\n- Ng&ocirc;n ngữ giao diện hỗ trợ: Nga, tiếng Anh, tiếng Đức, tiếng Hy Lạp, Ucraina, belarusian, Ba Lan, chineze, Bungary, Thổ Nhĩ Kỳ.<br />\r\n- Hỗ trợ từ điển b&ecirc;n ngo&agrave;i.<br />\r\n- Chỉnh sửa tập tin FB2 v&agrave; TXT.<br />\r\n- C&oacute; thể chọn trang m&atilde; cho t&ecirc;n tập tin trong zip.<br />\r\n- Bốn thiết lập sẵn với font chữ độc lập, m&agrave;u sắc, độ s&aacute;ng, chỉnh tương phản v&agrave; thụt lề của văn bản.<br />\r\n- Chế độ hai trang khi thiết bị xoay ngang.<br />\r\n- Cấu h&igrave;nh cho phong c&aacute;ch hiển thị: ti&ecirc;u đề, tr&iacute;ch dẫn, t&oacute;m tắt, vv... (mỗi phong c&aacute;ch hiển thị bao gồm font chữ, m&agrave;u sắc, kiểu chữ, cỡ chữ, đổ b&oacute;ng, thụt lề, khoảng c&aacute;ch...)<br />\r\n- Chức năng T&igrave;m kiếm.<br />\r\n- Di chuyển trong văn bản: theo phần trăm, theo trang, đến đầu / cuối văn bản, 10 trang về ph&iacute;a trước / sau, đến chương kế tiếp / trước.<br />\r\n- Hiển thị mục lục của fb2, doc v&agrave; epub.<br />\r\n- C&oacute; khả năng giữ đ&egrave;n m&agrave;n h&igrave;nh nền l&ecirc;n đến 20 ph&uacute;t khi kh&ocirc;ng hoạt động.<br />\r\n- Đồng bộ h&oacute;a vị tr&iacute; đọc qua mạng hoặc hệ thống tập tin.<br />\r\n<br />\r\n<strong>II. Ứng dụng eReader Prestigio: Book Reader</strong>&nbsp;[<a href=\"http://www.vn-zoom.com/register.php\" onclick=\"alert(\'You made the correct decision :)\')\"><input type=\"button\" value=\"Hãy đăng ký làm thành viên để xem được liên kết này\" /></a>&nbsp;]<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://lh3.googleusercontent.com/T5xwhe4ugBT8c90MHBrmoHG0qJSOgPIFKDepFLy5eT3YkUGqkOXf91FCvDpnWC7WbA=w300\" /></p>\r\n\r\n<p><br />\r\nPhi&ecirc;n bản hiện tại: 6.0.0.3<br />\r\nĐiểm đ&aacute;nh gi&aacute; tr&ecirc;n GP: 4.6<br />\r\nNh&agrave; ph&aacute;t triển: Prestigio [<a href=\"http://www.vn-zoom.com/register.php\" onclick=\"alert(\'You made the correct decision :)\')\"><input type=\"button\" value=\"Hãy đăng ký làm thành viên để xem được liên kết này\" /></a>&nbsp;]<br />\r\nĐịa chỉ cho PC tải về: [<a href=\"http://www.vn-zoom.com/register.php\" onclick=\"alert(\'You made the correct decision :)\')\"><input type=\"button\" value=\"Hãy đăng ký làm thành viên để xem được liên kết này\" /></a>&nbsp;]<br />\r\nDung lượng c&agrave;i đặt tr&ecirc;n thiết bị: 37,32 Mb<br />\r\n<br />\r\neReader Prestigio: Book Reader l&agrave; một ứng dụng đọc s&aacute;ch điện tử đa ng&ocirc;n ngữ, đa định dạng văn bản. Giao diện trực quan với hơn 25 ng&ocirc;n ngữ v&agrave; c&oacute; chức năng Text-To-Speech.<br />\r\n<br />\r\n☆ eReader Prestigio l&agrave; ứng dụng đọc s&aacute;ch chứa nội dung văn bản (Text), c&oacute; thể mở c&aacute;c tập tin văn bản trong nhiều định dạng bao gồm epub, html, FB2, fb2.zip, txt, pdf, mobi, epub3, djvu v&agrave; nhiều dạng văn bản kh&aacute;c; Audio Book định dạng MP3, AAC, M4B.<br />\r\n<br />\r\n☆ L&uacute;c mệt mỏi, bạn c&oacute; thể bật Text-To-Speech để ứng dụng đọc cuốn s&aacute;ch cho bạn nghe bằng giọng đọc du giường của chị Google! Những c&acirc;u chuyện cổ t&iacute;ch sẽ đưa những đứa trẻ của bạn đi v&agrave;o giấc mơ thiến thận... &agrave; nhầm... thi&ecirc;n thần.<br />\r\nV&agrave; c&ograve;n nhiều t&iacute;nh năng th&uacute; vị kh&aacute;c, mn tự kh&aacute;m ph&aacute;.<br />\r\n<br />\r\n<strong>III. Ứng dụng @Voice Aloud Reader</strong>&nbsp;[<a href=\"http://www.vn-zoom.com/register.php\" onclick=\"alert(\'You made the correct decision :)\')\"><input type=\"button\" value=\"Hãy đăng ký làm thành viên để xem được liên kết này\" /></a>&nbsp;]<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://lh6.ggpht.com/LP6ZbrckzcFBlPuH-CEkAWXgh8StKg-3M-a-bPB8Lo2KITpDP6VBahC7MSbD9Vx4VKU=w300\" /></p>\r\n\r\n<p><br />\r\nPhi&ecirc;n bản hiện tại: 12.06.01<br />\r\nĐiểm đ&aacute;nh gi&aacute; tr&ecirc;n GP: 4.4<br />\r\nNh&agrave; ph&aacute;t triển: Hyperionics Technology [<a href=\"http://www.vn-zoom.com/register.php\" onclick=\"alert(\'You made the correct decision :)\')\"><input type=\"button\" value=\"Hãy đăng ký làm thành viên để xem được liên kết này\" /></a>&nbsp;]<br />\r\nĐịa chỉ cho PC tải về: [<a href=\"http://www.vn-zoom.com/register.php\" onclick=\"alert(\'You made the correct decision :)\')\"><input type=\"button\" value=\"Hãy đăng ký làm thành viên để xem được liên kết này\" /></a>&nbsp;]<br />\r\nDung lượng c&agrave;i đặt tr&ecirc;n thiết bị: 38,04 Mb<br />\r\n<br />\r\nỨng dụng đọc to hoặc đọc c&aacute;c trang m&agrave;n h&igrave;nh web, tin tức, email d&agrave;i, TXT, PDF, DOC, DOCX, RTF, documens OpenOffice, EPUB, MOBI, PRC, AZW, ebooks FB2 v&agrave; nhiều hơn nữa. Đ&acirc;y l&agrave; một đầu đọc HTML, đọc t&agrave;i liệu v&agrave; đọc ebook tất cả trong một, cả cho việc đọc tr&ecirc;n m&agrave;n h&igrave;nh, hoặc nghe khi đ&ocirc;i mắt của bạn bị tổn thương, bị trục trặc hoặc đang bận rộn ở nơi kh&aacute;c.<br />\r\n<br />\r\nT&Iacute;NH NĂNG, ĐẶC ĐIỂM:<br />\r\n* Mở văn bản, PDF, DOC, DOCX, RTF, văn bản OpenOffice hoặc file HTML từ file hệ thống Android.<br />\r\n* Nhấn biểu tượng &quot;Chia sẻ&quot; trong bất kỳ ứng dụng n&agrave;o để gửi văn bản sang @Voice Aloud Reader đọc qua loa hoặc tai nghe.<br />\r\n* Khi đọc c&aacute;c trang web, c&aacute;c mục tr&igrave;nh đơn, chuyển hướng, quảng c&aacute;o, tin r&aacute;c kh&aacute;c được loại bỏ, c&ograve;n lại văn bản sạch để đọc hoặc nghe.<br />\r\n* Nếu ứng dụng kh&ocirc;ng c&oacute; sẵn biểu tượng &quot;Chia sẻ&quot;, c&oacute; thể sao ch&eacute;p văn bản trong ứng dụng đ&oacute; v&agrave; d&aacute;n n&oacute; v&agrave;o @Voice để đọc.<br />\r\n* Đọc ebook tr&ecirc;n m&agrave;n h&igrave;nh với sự t&ocirc; s&aacute;ng, chuyển c&aacute;c trang v&agrave; đọc li&ecirc;n tục. H&atilde;y gắn tai nghe, tắt m&agrave;n h&igrave;nh v&agrave; lắng nghe!<br />\r\n* Ghi lại nội dung th&agrave;nh file - WAV (kh&ocirc;ng n&eacute;n) hoặc (n&eacute;n), hỗ trợ định dạng OGG.<br />\r\n* Chỉnh Speech cho TTS, t&ugrave;y chọn sử dụng Regular Expressions (Regex)<br />\r\n* Desktop / m&aacute;y t&iacute;nh x&aacute;ch tay mở rộng tr&igrave;nh duyệt Google Chrome để th&ecirc;m b&agrave;i viết cho @Voice ch&egrave;n v&agrave;o danh s&aacute;ch đọc với một c&uacute; nhấp chuột.<br />\r\n* Dễ d&agrave;ng điều khiển &acirc;m lượng, s&acirc;n v&agrave; tốc độ của lời n&oacute;i.<br />\r\n* Tự động nhận ra ng&ocirc;n ngữ để sử dụng giọng n&oacute;i TTS th&iacute;ch hợp, nếu c&oacute;, hoặc thiết lập tiếng n&oacute;i ưa th&iacute;ch của bạn bằng tay với mục tr&igrave;nh đơn: &quot;Chọn ng&ocirc;n ngữ&quot;.<br />\r\n* T&ugrave;y chọn hẹn giờ ngủ để dừng đọc sau một thời gian nhất định.<br />\r\n<br />\r\nT&oacute;m lại:<br />\r\nCả 3 ứng dụng đều n&ecirc;n d&ugrave;ng, mỗi ứng dụng c&oacute; ưu thế ri&ecirc;ng v&agrave; bổ sung cho nhau.<br />\r\nVới thiết bị c&oacute; dung lượng nhỏ th&igrave; n&ecirc;n d&ugrave;ng Alreader, tuy c&oacute; hạn chế về định dạng s&aacute;ch điện tử, hoặc d&ugrave;ng @Voice Aloud Reader.<br />\r\nSử dụng th&ecirc;m ứng dụng vnSpeak TTS để bổ sung giọng đọc người thật. Tuy nhi&ecirc;n đối với m&igrave;nh th&igrave;:<br />\r\n&nbsp;</p>\r\n\r\n<p>Lỡ y&ecirc;u Gụ gỗ (Google) mất rồi,<br />\r\nHồng Vi m&atilde;i chỉ l&agrave; người đến sau.</p>\r\n\r\n<p><br />\r\nvnSpeak TTS: Bộ đọc tiếng Việt [<a href=\"http://www.vn-zoom.com/register.php\" onclick=\"alert(\'You made the correct decision :)\')\"><input type=\"button\" value=\"Hãy đăng ký làm thành viên để xem được liên kết này\" /></a>&nbsp;]<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://lh5.ggpht.com/Lx7p78mravSlOqylK9BWgSOTcADoii-Jm_h438ETvKe9kEKvMvbXVAMnAwxsSDLDPF0=w300\" /></p>\r\n\r\n<p><br />\r\nPhi&ecirc;n bản hiện tại: 3.5<br />\r\nĐiểm đ&aacute;nh gi&aacute; tr&ecirc;n GP: 4.2<br />\r\nĐịa chỉ cho PC tải về: [<a href=\"http://www.vn-zoom.com/register.php\" onclick=\"alert(\'You made the correct decision :)\')\"><input type=\"button\" value=\"Hãy đăng ký làm thành viên để xem được liên kết này\" /></a>&nbsp;]<br />\r\nDung lượng c&agrave;i đặt tr&ecirc;n thiết bị: 48,67 Mb</p>\r\n', '2018-05-21 15:52:41');
